int32 OS_ObjectIdFinalizeDelete(int32 operation_status, OS_object_token_t *token)
{
    osal_id_t final_id;
    FILE *log = fopen("/tmp/logs/1220.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER: OS_ObjectIdFinalizeDelete op_status=%d token=%p\n", (int)operation_status, (void *)token);
        if (token != NULL)
        {
            fprintf(log, "  token->obj_id=%lu\n", (unsigned long)token->obj_id);
        }
        fflush(log);
    }

    /* Clear the OSAL ID if successful - this returns the record to the pool */
    if (operation_status == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "  decision: operation_status == OS_SUCCESS -> set final_id = OS_OBJECT_ID_UNDEFINED\n");
            fflush(log);
        }

        final_id = OS_OBJECT_ID_UNDEFINED;
    }
    else
    {
        if (log)
        {
            fprintf(log, "  decision: operation_status != OS_SUCCESS -> restore final_id = token->obj_id\n");
            fflush(log);
        }

        /* this restores the original ID */
        final_id = token->obj_id;
    }

    if (log)
    {
        fprintf(log, "  before OS_ObjectIdTransactionFinish final_id=%lu\n", (unsigned long)final_id);
        fflush(log);
    }

    /* Either way we must unlock the object type */
    OS_ObjectIdTransactionFinish(token, &final_id);

    if (log)
    {
        fprintf(log, "  after OS_ObjectIdTransactionFinish final_id=%lu\n", (unsigned long)final_id);
        fflush(log);
    }

    /* Give event callback to the application */
    if (operation_status == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "  about to call OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, token->obj_id=%lu)\n", (unsigned long)token->obj_id);
            fflush(log);
        }

        OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, token->obj_id, NULL);

        if (log)
        {
            /* The event callback may call back into OS and produce nested entries; record return from notify so the trace shows nesting/recursion */
            fprintf(log, "  returned from OS_NotifyEvent\n");
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_ObjectIdFinalizeDelete returning op_status=%d\n", (int)operation_status);
        fclose(log);
    }

    return operation_status;
}
