int32 OS_ObjectIdFinalizeDelete(int32 operation_status, OS_object_token_t *token)
{
    osal_id_t final_id;
    FILE *log = NULL;

    /* Open the log file in append mode; tolerate failure to open so we do not change behavior */
    log = fopen("/tmp/logs/1220.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_ObjectIdFinalizeDelete - operation_status=%ld, token=%p\n", (long)operation_status, (void *)token);
        if (token != NULL)
        {
            fprintf(log, "  token->obj_id=%lu\n", (unsigned long)token->obj_id);
        }
        fflush(log);
    }

    /* Clear the OSAL ID if successful - this returns the record to the pool */
    if (operation_status == OS_SUCCESS)
    {
        final_id = OS_OBJECT_ID_UNDEFINED;
        if (log != NULL)
        {
            fprintf(log, "  action: success -> final_id = OS_OBJECT_ID_UNDEFINED\n");
            fflush(log);
        }
    }
    else
    {
        /* this restores the original ID */
        final_id = token->obj_id;
        if (log != NULL)
        {
            fprintf(log, "  action: failure -> restore final_id = %lu\n", (unsigned long)final_id);
            fflush(log);
        }
    }

    /* Either way we must unlock the object type */
    if (log != NULL)
    {
        fprintf(log, "  calling OS_ObjectIdTransactionFinish(token=%p, &final_id=%lu)\n", (void *)token, (unsigned long)final_id);
        fflush(log);
    }
    OS_ObjectIdTransactionFinish(token, &final_id);
    if (log != NULL)
    {
        fprintf(log, "  returned from OS_ObjectIdTransactionFinish\n");
        fflush(log);
    }

    /* Give event callback to the application */
    if (operation_status == OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "  calling OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, obj_id=%lu)\n", (unsigned long)token->obj_id);
            fflush(log);
        }

        /* This may invoke a user-supplied EventHandler which could re-enter OS API */
        OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, token->obj_id, NULL);

        if (log != NULL)
        {
            fprintf(log, "  returned from OS_NotifyEvent\n");
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT: OS_ObjectIdFinalizeDelete - returning %ld\n", (long)operation_status);
        fclose(log);
    }

    return operation_status;
}
