int32 OS_ObjectIdFinalizeDelete(int32 operation_status, OS_object_token_t *token)
{
    osal_id_t final_id;

    /* Logging probes to help reproduce potential indirect recursion via OS_NotifyEvent.
     * Writes human-readable trace records to /tmp/logs/1220.txt (append).
     * The logs show entry/exit, decision points, values of final_id, and when OS_NotifyEvent
     * is invoked/returned. A simple static depth counter is used to show nested re-entries
     * (if OS_NotifyEvent ends up calling back into this function).
     */
    FILE *logf = NULL;
    static int depth = 0; /* simple reentrancy depth counter for this function */

    /* Try opening the log file; if it fails, proceed without logging. */
    logf = fopen("/tmp/logs/1220.txt", "a");
    if (logf)
    {
        fprintf(logf, "ENTER OS_ObjectIdFinalizeDelete depth=%d operation_status=%d token=%p\n",
                depth, (int)operation_status, (void *)token);
        fflush(logf);
    }

    /* Increase depth to reflect current call frame */
    depth++;

    /* Clear the OSAL ID if successful - this returns the record to the pool */
    if (operation_status == OS_SUCCESS)
    {
        final_id = OS_OBJECT_ID_UNDEFINED;
        if (logf)
        {
            fprintf(logf, "  decision: SUCCESS -> final_id=OS_OBJECT_ID_UNDEFINED\n");
            fflush(logf);
        }
    }
    else
    {
        /* this restores the original ID */
        final_id = token->obj_id;
        if (logf)
        {
            fprintf(logf, "  decision: FAILURE -> restore final_id=%ld\n", (long)final_id);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "  before OS_ObjectIdTransactionFinish depth=%d final_id=%ld token=%p\n",
                depth, (long)final_id, (void *)token);
        fflush(logf);
    }

    /* Either way we must unlock the object type */
    OS_ObjectIdTransactionFinish(token, &final_id);

    if (logf)
    {
        fprintf(logf, "  after OS_ObjectIdTransactionFinish depth=%d final_id=%ld token=%p\n",
                depth, (long)final_id, (void *)token);
        fflush(logf);
    }

    /* Give event callback to the application */
    if (operation_status == OS_SUCCESS)
    {
        if (logf)
        {
            /* Log the fact that we're about to call the event handler. This is the critical
             * point where, if EventHandler is set to OS_NotifyEvent (or another path leads
             * back to OS_NotifyEvent), a recursive re-entry into this function can occur.
             */
            fprintf(logf, "  about to call OS_NotifyEvent depth=%d event=%d obj_id=%ld token=%p\n",
                    depth, (int)OS_EVENT_RESOURCE_DELETED, (long)token->obj_id, (void *)token);
            fflush(logf);
        }

        OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, token->obj_id, NULL);

        if (logf)
        {
            fprintf(logf, "  returned from OS_NotifyEvent depth=%d token=%p\n", depth, (void *)token);
            fflush(logf);
        }
    }

    /* Decrease depth and log exit */
    depth--;
    if (logf)
    {
        fprintf(logf, "EXIT OS_ObjectIdFinalizeDelete depth=%d operation_status=%d final_id=%ld token=%p\n",
                depth, (int)operation_status, (long)final_id, (void *)token);
        fflush(logf);
        fclose(logf);
    }

    return operation_status;
}
