int32 OS_ObjectIdFindNextMatch(OS_ObjectMatchFunc_t MatchFunc, void *arg, OS_object_token_t *token)
{
    int32               return_code;
    uint32              obj_count;
    OS_common_record_t *base;
    OS_common_record_t *record;

    /* Open log file for appending diagnostic information about control flow
     * and state that are relevant to the reported MISRA Rule 8.13 scenario. */
    {
        FILE *logf = fopen("/tmp/logs/1565.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTER: OS_ObjectIdFindNextMatch token=%p obj_type=%u obj_idx=%u obj_id=%u\n",
                    (void *)token,
                    (unsigned)token->obj_type,
                    (unsigned)token->obj_idx,
                    (unsigned)token->obj_id);
            fclose(logf);
        }
    }

    return_code   = OS_ERR_NAME_NOT_FOUND;
    base          = &OS_common_table[OS_GetBaseForObjectType(token->obj_type)];
    obj_count     = OS_GetMaxForObjectType(token->obj_type);
    token->obj_id = OS_OBJECT_ID_UNDEFINED;

    while (true)
    {
        ++token->obj_idx;

        if (token->obj_idx >= obj_count)
        {
            /* Log loop exit due to end of table */
            FILE *logf = fopen("/tmp/logs/1565.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "LOOP_EXIT_END token_idx=%u obj_count=%u\n",
                        (unsigned)token->obj_idx, (unsigned)obj_count);
                fclose(logf);
            }
            break;
        }

        record = OS_OBJECT_TABLE_GET(base, *token);

        /* Probe and log the record pointer and its active_id-defined status. */
        {
            int id_defined = 0;
            int match = 0;

            /* Safely evaluate whether the id is defined first (preserves original
             * short-circuit semantics where MatchFunc is only called when the id
             * is defined). */
            id_defined = OS_ObjectIdDefined(record->active_id) ? 1 : 0;

            FILE *logf = fopen("/tmp/logs/1565.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "ITER idx=%u record=%p id_defined=%d\n",
                        (unsigned)token->obj_idx, (void *)record, id_defined);
                fclose(logf);
            }

            if (id_defined)
            {
                /* Call the match function only if the id is defined, same as
                 * original code. Record the return value for logging and to
                 * preserve control flow. */
                match = MatchFunc(arg, token, record) ? 1 : 0;

                FILE *logf2 = fopen("/tmp/logs/1565.txt", "a");
                if (logf2 != NULL)
                {
                    fprintf(logf2, "MATCH_CALL idx=%u record=%p match=%d token_obj_id=%u\n",
                            (unsigned)token->obj_idx, (void *)record, match, (unsigned)token->obj_id);
                    fclose(logf2);
                }

                if (match)
                {
                    return_code   = OS_SUCCESS;
                    token->obj_id = record->active_id;

                    FILE *logf3 = fopen("/tmp/logs/1565.txt", "a");
                    if (logf3 != NULL)
                    {
                        fprintf(logf3, "FOUND idx=%u record=%p set_token_obj_id=%u return_code=%ld\n",
                                (unsigned)token->obj_idx, (void *)record,
                                (unsigned)token->obj_id, (long)return_code);
                        fclose(logf3);
                    }

                    break;
                }
            }
        }
    }

    /* Final exit log */
    {
        FILE *logf = fopen("/tmp/logs/1565.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "EXIT: OS_ObjectIdFindNextMatch return=%ld token=%p obj_idx=%u obj_id=%u\n",
                    (long)return_code, (void *)token, (unsigned)token->obj_idx, (unsigned)token->obj_id);
            fclose(logf);
        }
    }

    return return_code;
}
