int32 OS_ObjectIdGetBySearch(OS_lock_mode_t lock_mode, osal_objtype_t idtype, OS_ObjectMatchFunc_t MatchFunc, void *arg,
                             OS_object_token_t *token)
{
    int32 return_code;

    /* Local lightweight declarations so this function can write logs without
     * requiring additional includes at file scope. These are standard C
     * library prototypes used only for diagnostic logging. */
    typedef struct __FILE FILE;
    extern FILE *fopen(const char *, const char *);
    extern int fprintf(FILE *, const char *, ...);
    extern int fclose(FILE *);

    /* Helper macro-like local block to append a line to the log file. */
    do
    {
        FILE *log = fopen("/tmp/logs/2455.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_ObjectIdGetBySearch(lock_mode=%d, idtype=%d, MatchFunc=%p, arg=%p, token=%p)\n",
                    (int)lock_mode, (int)idtype, (void *)MatchFunc, arg, (void *)token);
            fclose(log);
        }
    } while (0);

    /* Preserve original behavior: start the transaction */
    OS_ObjectIdTransactionInit(lock_mode, idtype, token);

    do
    {
        FILE *log = fopen("/tmp/logs/2455.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After TransactionInit: token=%p\n", (void *)token);
            fclose(log);
        }
    } while (0);

    /* Find the next match as in the original implementation */
    return_code = OS_ObjectIdFindNextMatch(MatchFunc, arg, token);

    do
    {
        FILE *log = fopen("/tmp/logs/2455.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After FindNextMatch: return_code=%ld\n", (long)return_code);
            fclose(log);
        }
    } while (0);

    if (return_code == OS_SUCCESS)
    {
        /* Log control path and state before converting the token */
        do
        {
            FILE *log = fopen("/tmp/logs/2455.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Path: SUCCESS -> calling OS_ObjectIdConvertToken(token=%p)\n", (void *)token);
                fclose(log);
            }
        } while (0);

        /* The "ConvertToken" routine will return with the global lock
         * in a state appropriate for returning to the caller, as indicated
         * by the "lock_mode" parameter. */
        return_code = OS_ObjectIdConvertToken(token);

        do
        {
            FILE *log = fopen("/tmp/logs/2455.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "After ConvertToken: return_code=%ld\n", (long)return_code);
                fclose(log);
            }
        } while (0);
    }
    else
    {
        /* Log control path and state before cancelling the transaction */
        do
        {
            FILE *log = fopen("/tmp/logs/2455.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Path: FAILURE -> calling OS_ObjectIdTransactionCancel(token=%p) (return_code=%ld)\n",
                        (void *)token, (long)return_code);
                fclose(log);
            }
        } while (0);

        OS_ObjectIdTransactionCancel(token);

        do
        {
            FILE *log = fopen("/tmp/logs/2455.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "After TransactionCancel: token=%p\n", (void *)token);
                fclose(log);
            }
        } while (0);
    }

    do
    {
        FILE *log = fopen("/tmp/logs/2455.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT: OS_ObjectIdGetBySearch -> return_code=%ld\n", (long)return_code);
            fclose(log);
        }
    } while (0);

    return return_code;
}
