int32 OS_ObjectIdGetBySearch(OS_lock_mode_t lock_mode, osal_objtype_t idtype, OS_ObjectMatchFunc_t MatchFunc, void *arg,
                             OS_object_token_t *token)
{
    int32 return_code;
    FILE *logf = NULL;

    /* Try to open the log file. If the directory doesn't exist, attempt to create it. */
    logf = fopen("/tmp/logs/2455.txt", "a");
    if (logf == NULL)
    {
        /* Attempt to create the directory and retry. Best-effort only; failure is non-fatal. */
        (void)system("/bin/mkdir -p /tmp/logs");
        logf = fopen("/tmp/logs/2455.txt", "a");
    }

    if (logf != NULL)
    {
        /* Log entry and observed parameter state. Avoid printing function-pointer as data pointer (undefined in strict C),
         * instead log whether it's present. */
        (void)fprintf(logf, "OS_ObjectIdGetBySearch ENTRY: lock_mode=%d idtype=%d MatchFunc_present=%d arg=%p token=%p\n",
                      (int)lock_mode, (int)idtype, (MatchFunc != NULL) ? 1 : 0, (const void *)arg, (const void *)token);
        (void)fflush(logf);
    }

    /* Preserve original behavior: start transaction */
    OS_ObjectIdTransactionInit(lock_mode, idtype, token);

    if (logf != NULL)
    {
        (void)fprintf(logf, "After TransactionInit: token=%p\n", (const void *)token);
        (void)fflush(logf);
    }

    /* Call the search routine. This is where the reported violation can manifest when callers pass a const object
     * but the typedef accepts a non-const void *ref parameter. We log the argument pointer so callers' usage can be observed. */
    return_code = OS_ObjectIdFindNextMatch(MatchFunc, arg, token);

    if (logf != NULL)
    {
        (void)fprintf(logf, "After FindNextMatch: return_code=%ld\n", (long)return_code);
        (void)fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        if (logf != NULL)
        {
            (void)fprintf(logf, "Converting token (expected to release/adjust locks per lock_mode) token=%p\n", (const void *)token);
            (void)fflush(logf);
        }

        return_code = OS_ObjectIdConvertToken(token);

        if (logf != NULL)
        {
            (void)fprintf(logf, "After ConvertToken: return_code=%ld\n", (long)return_code);
            (void)fflush(logf);
        }
    }
    else
    {
        if (logf != NULL)
        {
            (void)fprintf(logf, "No match found, cancelling transaction for token=%p\n", (const void *)token);
            (void)fflush(logf);
        }

        OS_ObjectIdTransactionCancel(token);

        if (logf != NULL)
        {
            (void)fprintf(logf, "After TransactionCancel\n");
            (void)fflush(logf);
        }
    }

    if (logf != NULL)
    {
        (void)fprintf(logf, "OS_ObjectIdGetBySearch EXIT: return_code=%ld\n", (long)return_code);
        (void)fclose(logf);
    }

    return return_code;
}
