bool OS_ObjectIdIteratorGetNext(OS_object_iter_t *iter)
{
    OS_common_record_t *record;
    bool                got_next;
    FILE *log = fopen("/tmp/logs/585.txt", "a");

    got_next           = false;
    if (log)
    {
        fprintf(log, "Enter OS_ObjectIdIteratorGetNext: token.obj_idx=%d, limit=%d, token.obj_id=%u\n",
                iter->token.obj_idx, iter->limit, (unsigned)iter->token.obj_id);
    }

    iter->token.obj_id = OS_OBJECT_ID_UNDEFINED;

    do
    {
        ++iter->token.obj_idx;
        if (log)
        {
            fprintf(log, "Iter incremented: obj_idx=%d\n", iter->token.obj_idx);
        }

        if (iter->token.obj_idx >= iter->limit)
        {
            if (log)
            {
                fprintf(log, "Reached limit (%d). Breaking.\n", iter->limit);
            }
            break;
        }

        record = OS_OBJECT_TABLE_GET(iter->base, iter->token);
        if (log)
        {
            fprintf(log, "OS_OBJECT_TABLE_GET returned record=%p", (void *)record);
            if (record)
            {
                fprintf(log, ", active_id=%u\n", (unsigned)record->active_id);
            }
            else
            {
                fprintf(log, ", record=NULL\n");
            }
        }

        if (iter->match == NULL)
        {
            if (log)
            {
                fprintf(log, "No match function provided, accepting record at idx=%d\n", iter->token.obj_idx);
            }
            iter->token.obj_id = record->active_id;
            got_next           = true;
        }
        else
        {
            int match_ret = iter->match(iter->arg, &iter->token, record);
            if (log)
            {
                fprintf(log, "match(...) returned %d for idx=%d\n", match_ret, iter->token.obj_idx);
            }
            if (match_ret)
            {
                iter->token.obj_id = record->active_id;
                got_next           = true;
            }
        }
    } while (!got_next);

    if (log)
    {
        fprintf(log, "Exit OS_ObjectIdIteratorGetNext: got_next=%d, token.obj_id=%u, obj_idx=%d\n",
                got_next, (unsigned)iter->token.obj_id, iter->token.obj_idx);
        fclose(log);
    }

    return got_next;
}
