bool OS_ObjectIdIteratorGetNext(OS_object_iter_t *iter)
{
    OS_common_record_t *record;
    bool                got_next;
    FILE *log = fopen("/tmp/logs/585.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "ENTER OS_ObjectIdIteratorGetNext: token.obj_idx=%d limit=%d token.obj_id=%d base=%p\n",
                iter->token.obj_idx, iter->limit, iter->token.obj_id, (void *)iter->base);
        fflush(log);
    }

    got_next           = false;
    iter->token.obj_id = OS_OBJECT_ID_UNDEFINED;

    do
    {
        ++iter->token.obj_idx;
        if (log != NULL)
        {
            fprintf(log, "Loop start: token.obj_idx=%d limit=%d\n", iter->token.obj_idx, iter->limit);
            fflush(log);
        }

        if (iter->token.obj_idx >= iter->limit)
        {
            if (log != NULL)
            {
                fprintf(log, "Breaking: token.obj_idx (%d) >= limit (%d)\n", iter->token.obj_idx, iter->limit);
                fflush(log);
                fclose(log);
            }
            break;
        }

        record = OS_OBJECT_TABLE_GET(iter->base, iter->token);
        if (log != NULL)
        {
            fprintf(log, "OS_OBJECT_TABLE_GET returned record=%p\n", (void *)record);
            fflush(log);
        }

        if (iter->match == NULL)
        {
            if (log != NULL)
            {
                fprintf(log, "match is NULL: accepting current record\n");
                fflush(log);
            }

            /* Preserve original behavior: assign active_id and mark found */
            iter->token.obj_id = record->active_id;
            got_next           = true;

            if (log != NULL)
            {
                fprintf(log, "Accepted: token.obj_id=%d (from record->active_id)\n", record->active_id);
                fflush(log);
            }
        }
        else
        {
            /* Call the matcher exactly once as original code did */
            bool match_result = iter->match(iter->arg, &iter->token, record);

            if (log != NULL)
            {
                fprintf(log, "match returned: %d\n", (int)match_result);
                fflush(log);
            }

            if (match_result)
            {
                iter->token.obj_id = record->active_id;
                got_next           = true;

                if (log != NULL)
                {
                    fprintf(log, "Accepted by matcher: token.obj_id=%d\n", record->active_id);
                    fflush(log);
                }
            }
        }
    } while (!got_next);

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_ObjectIdIteratorGetNext: got_next=%d token.obj_idx=%d token.obj_id=%d\n",
                got_next, iter->token.obj_idx, iter->token.obj_id);
        fflush(log);
        fclose(log);
    }

    return got_next;
}
