bool OS_ObjectIdIteratorGetNext(OS_object_iter_t *iter)
{
    OS_common_record_t *record;
    bool                got_next;
    FILE *log = fopen("/tmp/logs/585.txt", "a");

    if (log != NULL)
    {
        /* Log entry and initial state */
        fprintf(log, "ENTER: OS_ObjectIdIteratorGetNext iter=%p start_obj_idx=%ld limit=%ld token.obj_id=%u\n",
                (void *)iter,
                (long)iter->token.obj_idx,
                (long)iter->limit,
                (unsigned)iter->token.obj_id);
        fflush(log);
    }

    got_next           = false;
    iter->token.obj_id = OS_OBJECT_ID_UNDEFINED;

    do
    {
        ++iter->token.obj_idx;
        if (log != NULL)
        {
            fprintf(log, "LOOP: advanced obj_idx=%ld\n", (long)iter->token.obj_idx);
            fflush(log);
        }

        if (iter->token.obj_idx >= iter->limit)
        {
            if (log != NULL)
            {
                fprintf(log, "BREAK: obj_idx (%ld) >= limit (%ld)\n", (long)iter->token.obj_idx, (long)iter->limit);
                fflush(log);
            }
            break;
        }

        record = OS_OBJECT_TABLE_GET(iter->base, iter->token);
        if (log != NULL)
        {
            fprintf(log, "RECORD: record=%p", (void *)record);
            if (record != NULL)
            {
                /* Accessing active_id is part of original logic; log it for evidence */
                fprintf(log, " active_id=%u", (unsigned)record->active_id);
            }
            fprintf(log, "\n");
            fflush(log);
        }

        if (iter->match == NULL || iter->match(iter->arg, &iter->token, record))
        {
            if (log != NULL)
            {
                fprintf(log, "MATCH: predicate accepted at obj_idx=%ld, assigning obj_id=%u\n",
                        (long)iter->token.obj_idx,
                        (unsigned)record->active_id);
                fflush(log);
            }

            iter->token.obj_id = record->active_id;
            got_next           = true;
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "MATCH: predicate rejected at obj_idx=%ld\n", (long)iter->token.obj_idx);
                fflush(log);
            }
        }
    } while (!got_next);

    if (log != NULL)
    {
        fprintf(log, "EXIT: got_next=%d final_obj_idx=%ld final_obj_id=%u\n",
                (int)got_next,
                (long)iter->token.obj_idx,
                (unsigned)iter->token.obj_id);
        fclose(log);
    }

    return got_next;
}
