int32 OS_ObjectIdIteratorInit(OS_ObjectMatchFunc_t matchfunc, void *matcharg, osal_objtype_t objtype,
                              OS_object_iter_t *iter)
{
    int32 status;
    unsigned int limit;
    void *base;
    FILE *f = NULL;

    /* Compute the same values as the original implementation so behavior is preserved */
    limit = (unsigned int)OS_GetMaxForObjectType(objtype);
    base = (void *)&OS_common_table[OS_GetBaseForObjectType(objtype)];

    /* Attempt to open the log file for appending. If the directory doesn't exist,
       try to create it and retry once. Any failure to log should not affect
       the original behavior of the function. */
    f = fopen("/tmp/logs/2628.txt", "a");
    if (f == NULL)
    {
        /* Try to create parent directory if possible, then retry opening the file. */
        /* Use a best-effort approach; if these functions are not available or fail,
           we silently continue to preserve original behavior. */
        (void)mkdir("/tmp/logs", 0777);
        f = fopen("/tmp/logs/2628.txt", "a");
    }

    if (f != NULL)
    {
        fprintf(f, "ENTER: OS_ObjectIdIteratorInit\n");
        fprintf(f, "  matchfunc=%p\n", (void *)matchfunc);
        fprintf(f, "  matcharg=%p\n", matcharg);
        fprintf(f, "  objtype=%u\n", (unsigned int)objtype);
        fprintf(f, "  iter=%p\n", (void *)iter);
        fprintf(f, "  computed limit=%u\n", limit);
        fprintf(f, "  computed base=%p\n", base);
        fflush(f);
    }

    /* Preserve original assignment order and semantics */
    iter->match = matchfunc;
    iter->arg   = matcharg;
    iter->limit = limit;
    iter->base  = base;

    /* Call the original transaction init and capture its return value */
    status = OS_ObjectIdTransactionInit(OS_LOCK_MODE_GLOBAL, objtype, &iter->token);

    if (f != NULL)
    {
        fprintf(f, "  OS_ObjectIdTransactionInit returned %d\n", (int)status);
        fprintf(f, "EXIT: OS_ObjectIdIteratorInit\n\n");
        fclose(f);
    }

    return status;
}
