int32 OS_ObjectIdIteratorInit(OS_ObjectMatchFunc_t matchfunc, void *matcharg, osal_objtype_t objtype,
                              OS_object_iter_t *iter)
{
    int32 status;
    unsigned base_index;
    FILE *logf = NULL;

    /* Attempt to open the log file. If this fails, continue silently to preserve original behavior. */
    logf = fopen("/tmp/logs/2628.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "ENTER: OS_ObjectIdIteratorInit()\n");
        fprintf(logf, "  parameters: matchfunc=%p matcharg=%p objtype=%u iter=%p\n",
                (void *)matchfunc, matcharg, (unsigned)objtype, (void *)iter);
    }

    /* Preserve original logic */
    iter->match = matchfunc;
    iter->arg   = matcharg;
    iter->limit = OS_GetMaxForObjectType(objtype);

    /* Capture base index before taking address for clearer logs */
    base_index = OS_GetBaseForObjectType(objtype);
    iter->base  = &OS_common_table[base_index];

    if (logf != NULL)
    {
        fprintf(logf, "  computed: limit=%lu base_index=%u base_ptr=%p\n",
                (unsigned long)iter->limit, base_index, (void *)iter->base);
    }

    /* Call the original transaction init and record its return value */
    status = OS_ObjectIdTransactionInit(OS_LOCK_MODE_GLOBAL, objtype, &iter->token);

    if (logf != NULL)
    {
        fprintf(logf, "  OS_ObjectIdTransactionInit returned=%ld token=%lu\n",
                (long)status, (unsigned long)iter->token);
        fflush(logf);
        fclose(logf);
    }

    return status;
}
