int32 OS_QueueCreate(osal_id_t *queue_id, const char *queue_name, osal_blockcount_t queue_depth, size_t data_size,
                     uint32 flags)
{
    int32                       return_code;
    OS_object_token_t           token;
    OS_queue_internal_record_t *queue;

    /* Open a log file for capturing control flow and state. Append so multiple runs accumulate evidence. */
    {
        FILE *log = fopen("/tmp/logs/2605.txt", "a");
        if (log)
        {
            fprintf(log, "ENTER: OS_QueueCreate\n");
            fprintf(log, "  inputs: queue_id=%p, queue_name=%p, queue_name_str=%s, queue_depth=%llu, data_size=%zu, flags=0x%08x\n",
                    (void *)queue_id,
                    (const void *)queue_name,
                    (queue_name != NULL) ? queue_name : "(null)",
                    (unsigned long long)queue_depth,
                    data_size,
                    (unsigned)flags);
            fclose(log);
        }
    }

    /* validate inputs */
    OS_CHECK_POINTER(queue_id);
    OS_CHECK_APINAME(queue_name);
    OS_CHECK_SIZE(data_size);
    ARGCHECK(queue_depth <= OS_QUEUE_MAX_DEPTH, OS_QUEUE_INVALID_SIZE);

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    /* Log just before calling allocation */
    {
        FILE *log = fopen("/tmp/logs/2605.txt", "a");
        if (log)
        {
            fprintf(log, "CALL: OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, %p ('%s'))\n",
                    (const void *)queue_name,
                    (queue_name != NULL) ? queue_name : "(null)");
            fclose(log);
        }
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, queue_name, &token);

    /* Log result of allocation */
    {
        FILE *log = fopen("/tmp/logs/2605.txt", "a");
        if (log)
        {
            fprintf(log, "RET: OS_ObjectIdAllocateNew -> return_code=%ld\n", (long)return_code);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        queue = OS_OBJECT_TABLE_GET(OS_queue_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, queue, queue_name, queue_name);

        /* Log the initialized table entry and the parameters that will be set */
        {
            FILE *log = fopen("/tmp/logs/2605.txt", "a");
            if (log)
            {
                fprintf(log, "INFO: OS_OBJECT_INIT done. queue ptr=%p, name ptr=%p ('%s')\n",
                        (void *)queue,
                        (const void *)queue_name,
                        (queue_name != NULL) ? queue_name : "(null)");
                fclose(log);
            }
        }

        queue->max_depth = queue_depth;
        queue->max_size  = data_size;

        /* Now call the OS-specific implementation.  This reads info from the queue table. */
        {
            FILE *log = fopen("/tmp/logs/2605.txt", "a");
            if (log)
            {
                fprintf(log, "CALL: OS_QueueCreate_Impl(token=%p, flags=0x%08x)\n", (void *)&token, (unsigned)flags);
                fclose(log);
            }
        }

        return_code = OS_QueueCreate_Impl(&token, flags);

        /* Log result of OS_QueueCreate_Impl and the internal queue state */
        {
            FILE *log = fopen("/tmp/logs/2605.txt", "a");
            if (log)
            {
                fprintf(log, "RET: OS_QueueCreate_Impl -> return_code=%ld\n", (long)return_code);
                if (queue != NULL)
                {
                    fprintf(log, "STATE: queue->max_depth=%llu, queue->max_size=%zu\n",
                            (unsigned long long)queue->max_depth,
                            queue->max_size);
                }
                fclose(log);
            }
        }

        /* Check result, finalize record, and unlock global table. */
        /* Log before finalizing */
        {
            FILE *log = fopen("/tmp/logs/2605.txt", "a");
            if (log)
            {
                fprintf(log, "CALL: OS_ObjectIdFinalizeNew(return_code_before=%ld, &token, queue_id=%p)\n", (long)return_code, (void *)queue_id);
                fclose(log);
            }
        }

        return_code = OS_ObjectIdFinalizeNew(return_code, &token, queue_id);

        /* Final log of return code and resulting queue_id */
        {
            FILE *log = fopen("/tmp/logs/2605.txt", "a");
            if (log)
            {
                fprintf(log, "RET: OS_ObjectIdFinalizeNew -> final_return_code=%ld, queue_id=%p\n",
                        (long)return_code,
                        (void *)queue_id);
                fprintf(log, "EXIT: OS_QueueCreate\n\n");
                fclose(log);
            }
        }
    }
    else
    {
        /* Allocation failed: log and exit with the error code. */
        FILE *log = fopen("/tmp/logs/2605.txt", "a");
        if (log)
        {
            fprintf(log, "ERROR: OS_ObjectIdAllocateNew failed with return_code=%ld. Exiting OS_QueueCreate.\n\n", (long)return_code);
            fclose(log);
        }
    }

    return return_code;
}
