int32 OS_QueueGet(osal_id_t queue_id, void *data, size_t size, size_t *size_copied, int32 timeout)
{
    OS_object_token_t           token;
    int32                       return_code;
    OS_queue_internal_record_t *queue;
    FILE *log = fopen("/tmp/logs/1358.txt", "a");

    /* Minimal entry log so the function invocation is always recorded */
    if (log != NULL)
    {
        fprintf(log, "OS_QueueGet entered: queue_id=%lu, data=%p, size=%zu, size_copied_ptr=%p, timeout=%ld\n",
                (unsigned long)queue_id, data, size, (void *)size_copied, (long)timeout);
        fclose(log);
    }

    /* Check Parameters */
    OS_CHECK_POINTER(data);
    OS_CHECK_POINTER(size_copied);
    OS_CHECK_SIZE(size);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, queue_id, &token);
    if (return_code == OS_SUCCESS)
    {
        queue = OS_OBJECT_TABLE_GET(OS_queue_table, token);

        /* Log object lookup result and queue properties */
        log = fopen("/tmp/logs/1358.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdGetById returned OS_SUCCESS; token=%p, queue_ptr=%p, queue->max_size=%zu\n",
                    (void *)&token, (void *)queue, (queue != NULL) ? queue->max_size : (size_t)0);
            fclose(log);
        }

        if (size < queue->max_size)
        {
            /*
            ** The buffer that the user is passing in is potentially too small
            */
            *size_copied = 0;
            return_code  = OS_QUEUE_INVALID_SIZE;

            /* Log the small-size branch and the value written to *size_copied */
            log = fopen("/tmp/logs/1358.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "size < queue->max_size branch taken: requested size=%zu, queue->max_size=%zu, *size_copied set to %zu, return_code=%ld\n",
                        size, queue->max_size, *size_copied, (long)return_code);
                fclose(log);
            }
        }
        else
        {
            /* Log that we are about to call the implementation layer */
            log = fopen("/tmp/logs/1358.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Calling OS_QueueGet_Impl: token=%p, data=%p, size=%zu, size_copied_ptr=%p, timeout=%ld\n",
                        (void *)&token, data, size, (void *)size_copied, (long)timeout);
                fclose(log);
            }

            return_code = OS_QueueGet_Impl(&token, data, size, size_copied, timeout);

            /* After call, log the returned code and the value written to *size_copied.
             * This captures the observable final state (even if intermediate writes
             * inside the impl were overwritten before returning).
             */
            log = fopen("/tmp/logs/1358.txt", "a");
            if (log != NULL)
            {
                /* Protect dereference: OS_QueueGet_Impl is expected to set *size_copied on success/failure paths
                 * so reading it here is used only for diagnostic logging as per original behavior.
                 */
                fprintf(log, "OS_QueueGet_Impl returned: return_code=%ld, final *size_copied=%zu\n",
                        (long)return_code, *size_copied);
                fclose(log);
            }
        }
    }
    else
    {
        /* Log failure to get object id */
        log = fopen("/tmp/logs/1358.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdGetById failed: return_code=%ld, queue_id=%lu\n", (long)return_code, (unsigned long)queue_id);
            fclose(log);
        }
    }

    /* Final exit log */
    log = fopen("/tmp/logs/1358.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_QueueGet exiting: return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
