int32 OS_QueueGetInfo(osal_id_t queue_id, OS_queue_prop_t *queue_prop)
{
    OS_common_record_t *record;
    int32               return_code;
    OS_object_token_t   token;

    /* Open log for appending control-flow/state information for reproduction */
    FILE *log = fopen("/tmp/logs/395.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_QueueGetInfo - queue_id=%llu, queue_prop=%p\n", (unsigned long long)queue_id, (void *)queue_prop);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(queue_prop);

    memset(queue_prop, 0, sizeof(OS_queue_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, queue_id, &token);
    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_queue_table, token);

        /* Log the state of the record as observed (read-only usage) */
        if (log)
        {
            /* Print pointer and field values to show that the record is only read */
            fprintf(log, "OS_QueueGetInfo: record_ptr=%p\n", (void *)record);

            /* Guarding the prints minimally to avoid abnormal logs if fields are unexpected */
            if (record != NULL)
            {
                /* name_entry is treated as a string by the implementation; log a safe prefix */
                fprintf(log, "OS_QueueGetInfo: record->name_entry=\"%s\", record->creator=%u\n",
                        record->name_entry ? record->name_entry : "(null)", (unsigned int)record->creator);

                fprintf(log, "OS_QueueGetInfo: observation - record fields only read (no writes performed)\n");
            }
            fflush(log);
        }

        strncpy(queue_prop->name, record->name_entry, sizeof(queue_prop->name) - 1);
        queue_prop->creator = record->creator;

        /*
         * Currently there are no additional queue details provided by the impl layer -
         * But this could be added in the future (i.e. current/max depth, msg size, etc)
         */

        OS_ObjectIdRelease(&token);
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_QueueGetInfo - return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
