int32 OS_QueueGetInfo(osal_id_t queue_id, OS_queue_prop_t *queue_prop)
{
    OS_common_record_t *record;
    int32               return_code;
    OS_object_token_t   token;
    FILE *logf = fopen("/tmp/logs/395.txt", "a");

    /* Minimal entry probe so the function invocation is always recorded */
    if (logf != NULL)
    {
        fprintf(logf, "OS_QueueGetInfo ENTER: queue_id=%d\n", (int)queue_id);
    }
    else
    {
        /* Fallback if the expected log directory is not present */
        FILE *fb = fopen("/tmp/395.txt", "a");
        if (fb != NULL)
        {
            fprintf(fb, "OS_QueueGetInfo ENTER (fallback): queue_id=%d\n", (int)queue_id);
            fclose(fb);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(queue_prop);

    memset(queue_prop, 0, sizeof(OS_queue_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, queue_id, &token);
    if (logf != NULL)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %d\n", (int)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_queue_table, token);

        if (logf != NULL)
        {
            fprintf(logf, "record pointer=%p\n", (void *)record);
        }

        if (record != NULL)
        {
            /* Log the values that will be read from the non-const record pointer. */
            if (logf != NULL)
            {
                const char *rname = (record->name_entry != NULL) ? record->name_entry : "(null)";
                fprintf(logf, "About to read record->name_entry='%s' and record->creator=%u\n",
                        rname, (unsigned)record->creator);
            }

            strncpy(queue_prop->name, record->name_entry, sizeof(queue_prop->name) - 1);
            queue_prop->creator = record->creator;

            if (logf != NULL)
            {
                fprintf(logf, "Copied to queue_prop: name='%s', creator=%u\n",
                        queue_prop->name, (unsigned)queue_prop->creator);
            }
        }
        else
        {
            if (logf != NULL)
            {
                fprintf(logf, "OS_OBJECT_TABLE_GET returned NULL record\n");
            }
        }

        /* Release the object id token as in the original logic */
        OS_ObjectIdRelease(&token);

        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdRelease called\n");
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_QueueGetInfo EXIT: return_code=%d\n\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
