int32 OS_QueuePut(osal_id_t queue_id, const void *data, size_t size, uint32 flags)
{
    OS_object_token_t           token;
    int32                       return_code;
    OS_queue_internal_record_t *queue;

    /* Minimal logging helper: append logs to file so other agents can inspect control flow and state */
    FILE *log = fopen("/tmp/logs/1955.txt", "a");
    if (log)
    {
        /* Log entry with parameters. Casts are used to avoid format warnings for unknown typedefs. */
        fprintf(log, "OS_QueuePut: entry: queue_id=%u, data=%p, size=%zu, flags=%u\n",
                (unsigned)queue_id, data, size, (unsigned)flags);
    }

    /* Check Parameters */
    OS_CHECK_POINTER(data);
    OS_CHECK_SIZE(size);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, queue_id, &token);
    if (log)
    {
        fprintf(log, "OS_QueuePut: ObjectIdGetById returned %ld\n", (long)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        queue = OS_OBJECT_TABLE_GET(OS_queue_table, token);

        if (log)
        {
            /* Log the retrieved queue pointer and the queue's configured max_size. This shows that
             * the local pointer 'queue' is only read from in this function (possible const-qualification).
             */
            fprintf(log, "OS_QueuePut: resolved queue=%p, queue->max_size=%zu\n", (void *)queue, (size_t)queue->max_size);
        }

        if (size > queue->max_size)
        {
            /*
            ** The buffer that the user is passing in is too large
            */
            if (log)
            {
                fprintf(log, "OS_QueuePut: size %zu > queue->max_size %zu -> returning OS_QUEUE_INVALID_SIZE\n",
                        size, (size_t)queue->max_size);
            }
            return_code = OS_QUEUE_INVALID_SIZE;
        }
        else
        {
            if (log)
            {
                fprintf(log, "OS_QueuePut: calling OS_QueuePut_Impl with data=%p, size=%zu, flags=%u\n",
                        data, size, (unsigned)flags);
            }

            return_code = OS_QueuePut_Impl(&token, data, size, flags);

            if (log)
            {
                fprintf(log, "OS_QueuePut: OS_QueuePut_Impl returned %ld\n", (long)return_code);
            }
        }
    }

    if (log)
    {
        fprintf(log, "OS_QueuePut: exit returning %ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
