int32 OS_SelectFdClear(OS_FdSet *Set, osal_id_t objid)
{
    int32        return_code;
    osal_index_t local_id;

    /* Minimal entry log (open/write/close immediately to avoid leaking handles
     * if OS_CHECK_POINTER returns early). Do not dereference Set here. */
    {
        FILE *log = fopen("/tmp/logs/2290.txt", "a");
        if (log)
        {
            fprintf(log, "OS_SelectFdClear: ENTER objid=%llu Set=%p\n", (unsigned long long)objid, (void *)Set);
            fclose(log);
        }
    }

    /* check parameters (preserve original behavior) */
    OS_CHECK_POINTER(Set);

    return_code = OS_ObjectIdToArrayIndex(OS_OBJECT_TYPE_OS_STREAM, objid, &local_id);

    /* Log the result of the ID->index translation */
    {
        FILE *log = fopen("/tmp/logs/2290.txt", "a");
        if (log)
        {
            fprintf(log, "OS_SelectFdClear: ObjectIdToArrayIndex returned %d local_id=%llu\n",
                    (int)return_code, (unsigned long long)local_id);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Compute element and bit position */
        size_t el = (size_t)(local_id >> 3);
        unsigned int shift = (unsigned int)(local_id & 0x7U);
        unsigned int mask = 1U << shift;

        /* Capture old value, perform clear, capture new value for logging */
        unsigned int old_val = Set->object_ids[el];
        unsigned int new_val = old_val & ~mask;

        /* Log the state before mutation */
        {
            FILE *log = fopen("/tmp/logs/2290.txt", "a");
            if (log)
            {
                fprintf(log, "OS_SelectFdClear: Clearing bit: el=%zu shift=%u mask=0x%02x old=0x%02x new=0x%02x\n",
                        el, shift, mask, old_val, new_val);
                fclose(log);
            }
        }

        /* Perform the original operation */
        Set->object_ids[el] = (uint8)new_val;
    }

    /* Final exit log */
    {
        FILE *log = fopen("/tmp/logs/2290.txt", "a");
        if (log)
        {
            fprintf(log, "OS_SelectFdClear: EXIT return_code=%d\n", (int)return_code);
            fclose(log);
        }
    }

    return return_code;
}
