int32 OS_SelectMultiple(OS_FdSet *ReadSet, OS_FdSet *WriteSet, int32 msecs)
{
    int32 return_code;

    /* Probe log file for control-flow/state tracing for Rule 14.2 investigation */
    {
        FILE *logf = fopen("/tmp/logs/1911.txt", "a");
        if (logf != NULL)
        {
            /* Record entry and the immediate observable state (pointers and timeout) */
            fprintf(logf, "[OS_SelectMultiple] ENTER\n");
            fprintf(logf, "  ReadSet pointer : %p\n", (void *)ReadSet);
            fprintf(logf, "  WriteSet pointer: %p\n", (void *)WriteSet);
            fprintf(logf, "  msecs           : %ld\n", (long)msecs);
            fflush(logf);
            fclose(logf);
        }
    }

    /* Preserve original behavior: delegate to implementation. */
    return_code = OS_SelectMultiple_Impl(ReadSet, WriteSet, msecs);

    /* Log the return value so callers can correlate entry/exit and reproduce the sequence. */
    {
        FILE *logf = fopen("/tmp/logs/1911.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "[OS_SelectMultiple] RETURN: %ld\n", (long)return_code);
            fprintf(logf, "----------------------------------------\n");
            fflush(logf);
            fclose(logf);
        }
    }

    return return_code;
}
