int32 OS_SelectMultiple(OS_FdSet *ReadSet, OS_FdSet *WriteSet, int32 msecs)
{
    int32 return_code;

    /*
     * Probe logging to capture entry, parameters and return value so that
     * other agents/tests can correlate control flow and state when
     * investigating remote violations (e.g. the reported default: break; case
     * in OS_API_Init). The log is appended to "/tmp/logs/1918.txt".
     *
     * The original behavior is preserved: the implementation is invoked
     * and its return value is returned unchanged.
     */
    {
        FILE *log = fopen("/tmp/logs/1918.txt", "a");
        if (log != NULL)
        {
            /* Write a minimal, robust record: function entry and parameters */
            fprintf(log, "OS_SelectMultiple ENTER: ReadSet=%p WriteSet=%p msecs=%ld\n",
                    (void *)ReadSet, (void *)WriteSet, (long)msecs);
            fflush(log);
            /* Keep the file open around the Impl call? We close after exit logging. */
            fclose(log);
        }
    }

    /* Preserve original behavior: call implementation and return its result. */
    return_code = OS_SelectMultiple_Impl(ReadSet, WriteSet, msecs);

    {
        FILE *log = fopen("/tmp/logs/1918.txt", "a");
        if (log != NULL)
        {
            /* Record the return value so test harnesses can correlate effects. */
            fprintf(log, "OS_SelectMultiple EXIT: return_code=%ld\n", (long)return_code);
            fflush(log);
            fclose(log);
        }
    }

    return return_code;
}
