int32 OS_SelectMultiple(OS_FdSet *ReadSet, OS_FdSet *WriteSet, int32 msecs)
{
    int32 return_code;
    /* Logging to help reproduce the reported dead-code scenario. */
    {
        FILE *log = fopen("/tmp/logs/1918.txt", "a");
        if (log != NULL)
        {
            /* Log entry and key state that influences the control path inside OS_DoSelect (msecs).
             * This helps other agents reproduce whether the code path that contains the memset(&ts_end,...) is taken.
             */
            fprintf(log, "OS_SelectMultiple entered: ReadSet=%p WriteSet=%p msecs=%d\n",
                    (void *)ReadSet, (void *)WriteSet, (int)msecs);

            if (msecs <= 0)
            {
                /* When msecs <= 0, the reported violation indicates the memset in OS_DoSelect's else branch is executed
                 * but ts_end is not subsequently read. Record this predicate so test runs can show the path was taken.
                 */
                fprintf(log, "Path decision: msecs <= 0 (expected to trigger OS_DoSelect else branch where memset(&ts_end,...) occurs)\n");
            }
            else
            {
                fprintf(log, "Path decision: msecs > 0 (expected to use ts_end for timeout calculations)\n");
            }

            fflush(log);
            fclose(log);
        }
    }

    /* Preserve the original behavior: delegate to the implementation and return its result. */
    return_code = OS_SelectMultiple_Impl(ReadSet, WriteSet, msecs);

    /* Log the result of the implementation call so the full control flow is visible in the logs. */
    {
        FILE *log = fopen("/tmp/logs/1918.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_SelectMultiple_Impl returned: %d\n", (int)return_code);
            fflush(log);
            fclose(log);
        }
    }

    return return_code;
}
