int32 OS_SelectMultiple_Impl(OS_FdSet *ReadSet, OS_FdSet *WriteSet, int32 msecs)
{
    fd_set wr_set;
    fd_set rd_set;
    int    maxfd;
    int32  return_code;

    /* Logging probe: append control flow and key state to file for reproduction */
    FILE *log = fopen("/tmp/logs/325.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_SelectMultiple_Impl: ReadSet=%p WriteSet=%p msecs=%d\n",
                (void *)ReadSet, (void *)WriteSet, (int)msecs);
        fflush(log);
    }

    FD_ZERO(&rd_set);
    FD_ZERO(&wr_set);
    maxfd = -1;
    if (ReadSet != NULL)
    {
        if (log) fprintf(log, "About to call OS_FdSet_ConvertIn_Impl(&maxfd=%d, &rd_set, ReadSet=%p)\n", maxfd, (void *)ReadSet);
        return_code = OS_FdSet_ConvertIn_Impl(&maxfd, &rd_set, ReadSet);
        if (log) fprintf(log, "OS_FdSet_ConvertIn_Impl returned %d, maxfd=%d\n", return_code, maxfd);
        if (return_code != OS_SUCCESS)
        {
            if (log)
            {
                fprintf(log, "EXIT OS_SelectMultiple_Impl early return %d (ReadSet conversion failed)\n", return_code);
                fclose(log);
            }
            return return_code;
        }
    }
    if (WriteSet != NULL)
    {
        if (log) fprintf(log, "About to call OS_FdSet_ConvertIn_Impl(&maxfd=%d, &wr_set, WriteSet=%p)\n", maxfd, (void *)WriteSet);
        return_code = OS_FdSet_ConvertIn_Impl(&maxfd, &wr_set, WriteSet);
        if (log) fprintf(log, "OS_FdSet_ConvertIn_Impl returned %d, maxfd=%d\n", return_code, maxfd);
        if (return_code != OS_SUCCESS)
        {
            if (log)
            {
                fprintf(log, "EXIT OS_SelectMultiple_Impl early return %d (WriteSet conversion failed)\n", return_code);
                fclose(log);
            }
            return return_code;
        }
    }

    if (maxfd >= 0)
    {
        if (log) fprintf(log, "Calling OS_DoSelect(maxfd=%d, msecs=%d)\n", maxfd, (int)msecs);
        return_code = OS_DoSelect(maxfd, &rd_set, &wr_set, msecs);
        if (log) fprintf(log, "OS_DoSelect returned %d\n", return_code);
    }
    else
    {
        /*
         * This return code will be used if the set(s) were
         * both empty/NULL or otherwise did not contain valid filehandles.
         */
        if (log) fprintf(log, "No fds found (maxfd=%d). Setting return_code=OS_ERR_INVALID_ID\n", maxfd);
        return_code = OS_ERR_INVALID_ID;
    }

    if (return_code == OS_SUCCESS)
    {
        if (ReadSet != NULL)
        {
            if (log) fprintf(log, "Calling OS_FdSet_ConvertOut_Impl(&rd_set, ReadSet=%p)\n", (void *)ReadSet);
            OS_FdSet_ConvertOut_Impl(&rd_set, ReadSet);
            if (log) fprintf(log, "OS_FdSet_ConvertOut_Impl completed for ReadSet=%p\n", (void *)ReadSet);
        }
        if (WriteSet != NULL)
        {
            if (log) fprintf(log, "Calling OS_FdSet_ConvertOut_Impl(&wr_set, WriteSet=%p)\n", (void *)WriteSet);
            OS_FdSet_ConvertOut_Impl(&wr_set, WriteSet);
            if (log) fprintf(log, "OS_FdSet_ConvertOut_Impl completed for WriteSet=%p\n", (void *)WriteSet);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT OS_SelectMultiple_Impl returning %d\n", return_code);
        fclose(log);
    }

    return return_code;
}
