int32 OS_SelectSingle(osal_id_t objid, uint32 *StateFlags, int32 msecs)
{
    int32             return_code;
    OS_object_token_t token;

    /* Logging probe: record entry and parameters */
    {
        FILE *log = fopen("/tmp/logs/1005.txt", "a");
        if (log != NULL)
        {
            /* Print basic call info. Casts used for portability. */
            fprintf(log, "OS_SelectSingle: ENTER objid=%lu StateFlags=%p msecs=%d\n",
                    (unsigned long) objid, (void *) StateFlags, (int) msecs);
            if (StateFlags != NULL)
            {
                /* Log current value pointed to by StateFlags (read-only probe) */
                fprintf(log, "OS_SelectSingle: initial *StateFlags=0x%08x\n", (unsigned) *StateFlags);
            }
            fclose(log);
        }
    }

    /* check parameters */
    OS_CHECK_POINTER(StateFlags);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_REFCOUNT, OS_OBJECT_TYPE_OS_STREAM, objid, &token);

    /* Log result of ObjectIdGetById */
    {
        FILE *log = fopen("/tmp/logs/1005.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_SelectSingle: OS_ObjectIdGetById returned %d\n", (int) return_code);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Log that we will call the implementation and the token address */
        {
            FILE *log = fopen("/tmp/logs/1005.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_SelectSingle: calling OS_SelectSingle_Impl with token=%p StateFlags=%p msecs=%d\n",
                        (void *) &token, (void *) StateFlags, (int) msecs);
                fclose(log);
            }
        }

        return_code = OS_SelectSingle_Impl(&token, StateFlags, msecs);

        /* Log the result from the implementation */
        {
            FILE *log = fopen("/tmp/logs/1005.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_SelectSingle: OS_SelectSingle_Impl returned %d\n", (int) return_code);
                fclose(log);
            }
        }

        OS_ObjectIdRelease(&token);

        /* Log that the token was released */
        {
            FILE *log = fopen("/tmp/logs/1005.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_SelectSingle: OS_ObjectIdRelease called for token=%p\n", (void *) &token);
                fclose(log);
            }
        }
    }

    /* Final exit log */
    {
        FILE *log = fopen("/tmp/logs/1005.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_SelectSingle: EXIT returning %d\n", (int) return_code);
            fclose(log);
        }
    }

    return return_code;
}
