int32 OS_SelectSingle_Impl(const OS_object_token_t *token, uint32 *SelectFlags, int32 msecs)
{
    int32                           return_code;
    fd_set                          wr_set;
    fd_set                          rd_set;
    OS_impl_file_internal_record_t *impl;
    FILE *log = fopen("/tmp/logs/425.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER: OS_SelectSingle_Impl token=%lu SelectFlags_ptr=%p SelectFlags_val=%lu msecs=%d\n",
                (unsigned long)(token ? *token : 0UL), (void *)SelectFlags,
                (unsigned long)(SelectFlags ? *SelectFlags : 0UL), (int)msecs);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (log)
    {
        fprintf(log, "LOOKUP: impl_ptr=%p\n", (void *)impl);
        if (impl != NULL)
        {
            fprintf(log, "IMPL STATE: selectable=%d fd=%d\n",
                    (int)impl->selectable, (int)impl->fd);
        }
        else
        {
            fprintf(log, "IMPL STATE: impl is NULL\n");
        }
    }

    /*
     * If called on a stream_id which does not support this
     * operation, return immediately and do not invoke the system call
     */
    if (!impl->selectable)
    {
        if (log)
        {
            fprintf(log, "EXIT EARLY: not selectable -> return %d\n", (int)OS_ERR_OPERATION_NOT_SUPPORTED);
            fclose(log);
        }
        return OS_ERR_OPERATION_NOT_SUPPORTED;
    }

    if (impl->fd >= FD_SETSIZE)
    {
        /* out of range of select() implementation */
        if (log)
        {
            fprintf(log, "EXIT EARLY: fd (%d) >= FD_SETSIZE (%d) -> return %d\n",
                    (int)impl->fd, (int)FD_SETSIZE, (int)OS_ERR_OPERATION_NOT_SUPPORTED);
            fclose(log);
        }
        return OS_ERR_OPERATION_NOT_SUPPORTED;
    }

    if (SelectFlags == NULL)
    {
        if (log)
        {
            fprintf(log, "WARNING: SelectFlags is NULL; treating as 0 -> return %d\n", (int)OS_SUCCESS);
            fclose(log);
        }
        return OS_SUCCESS;
    }

    if (*SelectFlags != 0)
    {
        if (log)
        {
            fprintf(log, "BRANCH: SelectFlags initial=0x%08lX -> preparing fd_sets for fd=%d\n",
                    (unsigned long)*SelectFlags, (int)impl->fd);
        }

        FD_ZERO(&wr_set);
        FD_ZERO(&rd_set);
        if (*SelectFlags & OS_STREAM_STATE_READABLE)
        {
            FD_SET(impl->fd, &rd_set);
            if (log) fprintf(log, "FD_SET: added fd %d to read set\n", (int)impl->fd);
        }
        if (*SelectFlags & OS_STREAM_STATE_WRITABLE)
        {
            FD_SET(impl->fd, &wr_set);
            if (log) fprintf(log, "FD_SET: added fd %d to write set\n", (int)impl->fd);
        }

        return_code = OS_DoSelect(impl->fd, &rd_set, &wr_set, msecs);

        if (log)
        {
            fprintf(log, "OS_DoSelect returned %d\n", (int)return_code);
        }

        if (return_code == OS_SUCCESS)
        {
            int rd_is_set = FD_ISSET(impl->fd, &rd_set) ? 1 : 0;
            int wr_is_set = FD_ISSET(impl->fd, &wr_set) ? 1 : 0;

            if (log)
            {
                fprintf(log, "SELECT RESULT: rd_is_set=%d wr_is_set=%d\n", rd_is_set, wr_is_set);
            }

            if (!rd_is_set)
            {
                *SelectFlags &= ~OS_STREAM_STATE_READABLE;
                if (log) fprintf(log, "UPDATED SelectFlags: cleared READABLE -> 0x%08lX\n", (unsigned long)*SelectFlags);
            }
            if (!wr_is_set)
            {
                *SelectFlags &= ~OS_STREAM_STATE_WRITABLE;
                if (log) fprintf(log, "UPDATED SelectFlags: cleared WRITABLE -> 0x%08lX\n", (unsigned long)*SelectFlags);
            }
        }
        else
        {
            *SelectFlags = 0;
            if (log) fprintf(log, "ERROR: select failed, zeroing SelectFlags and returning %d\n", (int)return_code);
        }
    }
    else
    {
        /* Nothing to check for, return immediately. */
        if (log) fprintf(log, "BRANCH: SelectFlags==0 -> nothing to check, returning OS_SUCCESS\n");
        return_code = OS_SUCCESS;
    }

    if (log)
    {
        fprintf(log, "EXIT: return_code=%d final SelectFlags=0x%08lX\n", (int)return_code, (unsigned long)*SelectFlags);
        fclose(log);
    }

    return return_code;
}
