int32 OS_SelectSingle_Impl(const OS_object_token_t *token, uint32 *SelectFlags, int32 msecs)
{
    int32                           return_code;
    fd_set                          wr_set;
    fd_set                          rd_set;
    OS_impl_file_internal_record_t *impl;

    /* Open log file for appending; ignore logging failures to preserve original behavior */
    FILE *log = fopen("/tmp/logs/425.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_SelectSingle_Impl token=%p SelectFlags=%p (*SelectFlags=0x%08x) msecs=%d\n",
                (const void *)token, (void *)SelectFlags, (unsigned int)(SelectFlags ? *SelectFlags : 0u), (int)msecs);
        fflush(log);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (log)
    {
        /* Log the pointer returned from the table lookup and some of its fields (read-only) */
        fprintf(log, "LOOKUP: impl=%p", (void *)impl);
        if (impl)
        {
            fprintf(log, " selectable=%d fd=%d", (int)impl->selectable, (int)impl->fd);
        }
        fprintf(log, "\n");
        fflush(log);
    }

    /*
     * If called on a stream_id which does not support this
     * operation, return immediately and do not invoke the system call
     */
    if (!impl->selectable)
    {
        if (log)
        {
            fprintf(log, "BRANCH: not selectable -> return OS_ERR_OPERATION_NOT_SUPPORTED\n");
            fflush(log);
            fclose(log);
        }
        return OS_ERR_OPERATION_NOT_SUPPORTED;
    }

    if (impl->fd >= FD_SETSIZE)
    {
        /* out of range of select() implementation */
        if (log)
        {
            fprintf(log, "BRANCH: fd (%d) >= FD_SETSIZE (%d) -> return OS_ERR_OPERATION_NOT_SUPPORTED\n",
                    (int)impl->fd, (int)FD_SETSIZE);
            fflush(log);
            fclose(log);
        }
        return OS_ERR_OPERATION_NOT_SUPPORTED;
    }

    if (*SelectFlags != 0)
    {
        FD_ZERO(&wr_set);
        FD_ZERO(&rd_set);
        if (*SelectFlags & OS_STREAM_STATE_READABLE)
        {
            FD_SET(impl->fd, &rd_set);
            if (log) { fprintf(log, "ACTION: mark fd %d for READ\n", (int)impl->fd); fflush(log); }
        }
        if (*SelectFlags & OS_STREAM_STATE_WRITABLE)
        {
            FD_SET(impl->fd, &wr_set);
            if (log) { fprintf(log, "ACTION: mark fd %d for WRITE\n", (int)impl->fd); fflush(log); }
        }

        return_code = OS_DoSelect(impl->fd, &rd_set, &wr_set, msecs);

        if (log)
        {
            fprintf(log, "RETURN from OS_DoSelect: return_code=%d\n", (int)return_code);
            fflush(log);
        }

        if (return_code == OS_SUCCESS)
        {
            if (!FD_ISSET(impl->fd, &rd_set))
            {
                *SelectFlags &= ~OS_STREAM_STATE_READABLE;
                if (log) { fprintf(log, "RESULT: fd %d not readable -> clear READABLE flag, SelectFlags=0x%08x\n", (int)impl->fd, (unsigned int)*SelectFlags); fflush(log); }
            }
            if (!FD_ISSET(impl->fd, &wr_set))
            {
                *SelectFlags &= ~OS_STREAM_STATE_WRITABLE;
                if (log) { fprintf(log, "RESULT: fd %d not writable -> clear WRITABLE flag, SelectFlags=0x%08x\n", (int)impl->fd, (unsigned int)*SelectFlags); fflush(log); }
            }
        }
        else
        {
            *SelectFlags = 0;
            if (log) { fprintf(log, "ERROR: OS_DoSelect failed -> clear all SelectFlags\n"); fflush(log); }
        }
    }
    else
    {
        /* Nothing to check for, return immediately. */
        return_code = OS_SUCCESS;
        if (log) { fprintf(log, "BRANCH: no flags set -> return OS_SUCCESS\n"); fflush(log); }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_SelectSingle_Impl return_code=%d SelectFlags=0x%08x impl=%p selectable=%d fd=%d\n",
                (int)return_code, (unsigned int)(SelectFlags ? *SelectFlags : 0u), (void *)impl,
                impl ? (int)impl->selectable : -1, impl ? (int)impl->fd : -1);
        fflush(log);
        fclose(log);
    }

    return return_code;
}
