int32 OS_SocketAccept(osal_id_t sock_id, osal_id_t *connsock_id, OS_SockAddr_t *Addr, int32 timeout)
{
    OS_common_record_t *         sock_record;
    OS_common_record_t *         conn_record;
    OS_stream_internal_record_t *sock;
    OS_stream_internal_record_t *conn;
    OS_object_token_t            sock_token;
    OS_object_token_t            conn_token;
    int32                        return_code;

    /* Open log for tracing control flow/state for violation repro */
    FILE *log = NULL;
    log = fopen("/tmp/logs/3288.txt", "a");
    if (log)
    {
        fprintf(log, "OS_SocketAccept ENTER: sock_id=%u, connsock_id_ptr=%p, Addr_ptr=%p\n",
                (unsigned)sock_id, (void *)connsock_id, (void *)Addr);
    }

    /* Check Parameters */
    OS_CHECK_POINTER(Addr);
    OS_CHECK_POINTER(connsock_id);

    /*
     * Note: setting "connrecord" here avoids a false warning
     * from static analysis tools about the value being
     * possibly used uninitialized (it cannot be, because
     * return_code is checked, and return_code is only
     * set to OS_SUCCESS when connrecord is also initialized)
     */
    conn_record = NULL;
    sock_record = NULL;
    sock        = NULL; /* <-- initialization that is claimed to be dead */
    conn        = NULL;
    memset(&sock_token, 0, sizeof(sock_token));
    memset(&conn_token, 0, sizeof(conn_token));

    if (log)
    {
        fprintf(log, "After init: sock_record=%p, conn_record=%p, sock=%p, conn=%p, sock_token_addr=%p, conn_token_addr=%p\n",
                (void *)sock_record, (void *)conn_record, (void *)sock, (void *)conn, (void *)&sock_token, (void *)&conn_token);
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, sock_id, &sock_token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d for sock_id=%u\n", (int)return_code, (unsigned)sock_id);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        sock_record = OS_OBJECT_TABLE_GET(OS_global_stream_table, sock_token);
        sock        = OS_OBJECT_TABLE_GET(OS_stream_table, sock_token);

        if (log)
        {
            fprintf(log, "After table get: sock_record=%p, sock=%p\n", (void *)sock_record, (void *)sock);
            if (sock)
            {
                /* Print a couple of fields to show they get overwritten after initial NULL */
                fprintf(log, "sock->socket_domain=%d, sock->socket_type=%d, sock->stream_state=0x%X, sock->stream_name=%p\n",
                        (int)sock->socket_domain, (int)sock->socket_type, (unsigned)sock->stream_state, (void *)sock->stream_name);
            }
            fflush(log);
        }

        if (sock->socket_type != OS_SocketType_STREAM)
        {
            /* Socket must be of the STREAM variety */
            if (log)
            {
                fprintf(log, "Incorrect object type: socket_type=%d\n", (int)sock->socket_type);
                fflush(log);
            }
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
        }
        else if ((sock->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != OS_STREAM_STATE_BOUND)
        {
            /* Socket must be bound but not connected */
            if (log)
            {
                fprintf(log, "Incorrect object state: stream_state=0x%X\n", (unsigned)sock->stream_state);
                fflush(log);
            }
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            /* Now create a unique ID for the connection */
            return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, NULL, &conn_token);
            if (log)
            {
                fprintf(log, "OS_ObjectIdAllocateNew returned %d\n", (int)return_code);
                fflush(log);
            }

            if (return_code == OS_SUCCESS)
            {
                conn_record = OS_OBJECT_TABLE_GET(OS_global_stream_table, conn_token);
                conn        = OS_OBJECT_TABLE_GET(OS_stream_table, conn_token);

                if (log)
                {
                    fprintf(log, "New conn token: conn_record=%p, conn=%p\n", (void *)conn_record, (void *)conn);
                    fflush(log);
                }

                /* Incr the refcount to record the fact that an operation is pending on this */
                memset(conn, 0, sizeof(OS_stream_internal_record_t));

                if (log)
                {
                    fprintf(log, "Conn cleared via memset, conn->socket_domain=%d, conn->socket_type=%d (after memset)\n",
                            (int)conn->socket_domain, (int)conn->socket_type);
                    fflush(log);
                }

                conn->socket_domain = sock->socket_domain;
                conn->socket_type   = sock->socket_type;

                OS_SocketAddrInit_Impl(Addr, sock->socket_domain);

                if (log)
                {
                    fprintf(log, "Calling OS_SocketAccept_Impl for sock_token_addr=%p conn_token_addr=%p\n", (void *)&sock_token, (void *)&conn_token);
                    fflush(log);
                }

                return_code = OS_SocketAccept_Impl(&sock_token, &conn_token, Addr, timeout);

                if (log)
                {
                    fprintf(log, "OS_SocketAccept_Impl returned %d\n", (int)return_code);
                    fflush(log);
                }

                if (return_code == OS_SUCCESS)
                {
                    /* Generate an entry name based on the remote address */
                    OS_CreateSocketName(&conn_token, Addr, sock_record->name_entry);
                    conn_record->name_entry = conn->stream_name;
                    conn->stream_state |= OS_STREAM_STATE_CONNECTED;

                    if (log)
                    {
                        fprintf(log, "Accept success: conn->stream_state=0x%X, conn_record->name_entry=%p, conn->stream_name=%p\n",
                                (unsigned)conn->stream_state, (void *)conn_record->name_entry, (void *)conn->stream_name);
                        fflush(log);
                    }
                }

                if (log)
                {
                    fprintf(log, "Finalizing new object with return_code=%d\n", (int)return_code);
                    fflush(log);
                }

                return_code = OS_ObjectIdFinalizeNew(return_code, &conn_token, connsock_id);

                if (log)
                {
                    fprintf(log, "OS_ObjectIdFinalizeNew returned %d, connsock_id=%p (out)\n", (int)return_code, (void *)connsock_id);
                    fflush(log);
                }
            }
        }

        OS_ObjectIdRelease(&sock_token);

        if (log)
        {
            fprintf(log, "Released sock_token; about to return %d\n", (int)return_code);
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_ObjectIdGetById failed, skipping socket handling, return_code=%d\n", (int)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_SocketAccept EXIT: return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
