int32 OS_SocketAccept(osal_id_t sock_id, osal_id_t *connsock_id, OS_SockAddr_t *Addr, int32 timeout)
{
    OS_common_record_t *         sock_record;
    OS_common_record_t *         conn_record;
    OS_stream_internal_record_t *sock;
    OS_stream_internal_record_t *conn;
    OS_object_token_t            sock_token;
    OS_object_token_t            conn_token;
    int32                        return_code;

    FILE *log = fopen("/tmp/logs/3285.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_SocketAccept(sock_id=%lu, connsock_id=%p, Addr=%p, timeout=%d)\n",
                (unsigned long)sock_id, (void *)connsock_id, (void *)Addr, (int)timeout);
        fflush(log);
    }

    /* Check Parameters */
    OS_CHECK_POINTER(Addr);
    OS_CHECK_POINTER(connsock_id);

    /*
     * Note: setting "connrecord" here avoids a false warning
     * from static analysis tools about the value being
     * possibly used uninitialized (it cannot be, because
     * return_code is checked, and return_code is only
     * set to OS_SUCCESS when connrecord is also initialized)
     */
    conn_record = NULL;
    sock_record = NULL;
    sock        = NULL;
    conn        = NULL;
    memset(&sock_token, 0, sizeof(sock_token));
    memset(&conn_token, 0, sizeof(conn_token));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, sock_id, &sock_token);
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d for sock_id=%lu\n", (int)return_code, (unsigned long)sock_id);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        sock_record = OS_OBJECT_TABLE_GET(OS_global_stream_table, sock_token);
        sock        = OS_OBJECT_TABLE_GET(OS_stream_table, sock_token);

        if (log)
        {
            fprintf(log, "Obtained sock_record=%p sock=%p sock_token=%p\n",
                    (void *)sock_record, (void *)sock, (void *)&sock_token);
            if (sock != NULL)
            {
                fprintf(log, "sock->socket_type=%d sock->stream_state=0x%X sock->socket_domain=%d\n",
                        (int)sock->socket_type, (unsigned int)sock->stream_state, (int)sock->socket_domain);
            }
            fflush(log);
        }

        if (sock->socket_type != OS_SocketType_STREAM)
        {
            /* Socket must be of the STREAM variety */
            if (log)
            {
                fprintf(log, "BRANCH: incorrect object type (not STREAM).\n");
                fflush(log);
            }
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
        }
        else if ((sock->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != OS_STREAM_STATE_BOUND)
        {
            /* Socket must be bound but not connected */
            if (log)
            {
                fprintf(log, "BRANCH: incorrect object state (stream_state=0x%X).\n", (unsigned int)sock->stream_state);
                fflush(log);
            }
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            /* Now create a unique ID for the connection */
            return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, NULL, &conn_token);
            if (log)
            {
                fprintf(log, "OS_ObjectIdAllocateNew returned %d conn_token=%p\n", (int)return_code, (void *)&conn_token);
                fflush(log);
            }

            if (return_code == OS_SUCCESS)
            {
                conn_record = OS_OBJECT_TABLE_GET(OS_global_stream_table, conn_token);
                conn        = OS_OBJECT_TABLE_GET(OS_stream_table, conn_token);

                /* Incr the refcount to record the fact that an operation is pending on this */
                memset(conn, 0, sizeof(OS_stream_internal_record_t));

                conn->socket_domain = sock->socket_domain;
                conn->socket_type   = sock->socket_type;

                if (log)
                {
                    fprintf(log, "Allocated conn_record=%p conn=%p; copied socket_domain=%d socket_type=%d\n",
                            (void *)conn_record, (void *)conn, (int)conn->socket_domain, (int)conn->socket_type);
                    fflush(log);
                }

                OS_SocketAddrInit_Impl(Addr, sock->socket_domain);

                if (log)
                {
                    fprintf(log, "Calling OS_SocketAccept_Impl with sock_token=%p conn_token=%p Addr=%p timeout=%d\n",
                            (void *)&sock_token, (void *)&conn_token, (void *)Addr, (int)timeout);
                    fflush(log);
                }

                return_code = OS_SocketAccept_Impl(&sock_token, &conn_token, Addr, timeout);

                if (log)
                {
                    fprintf(log, "OS_SocketAccept_Impl returned %d\n", (int)return_code);
                    if (conn != NULL)
                    {
                        fprintf(log, "After accept: conn->stream_state=0x%X conn->stream_name=%p\n",
                                (unsigned int)conn->stream_state, (void *)conn->stream_name);
                    }
                    if (sock_record != NULL)
                    {
                        fprintf(log, "sock_record->name_entry=%p\n", (void *)sock_record->name_entry);
                    }
                    fflush(log);
                }

                if (return_code == OS_SUCCESS)
                {
                    /* Generate an entry name based on the remote address */
                    OS_CreateSocketName(&conn_token, Addr, sock_record->name_entry);
                    conn_record->name_entry = conn->stream_name;
                    conn->stream_state |= OS_STREAM_STATE_CONNECTED;

                    if (log)
                    {
                        fprintf(log, "Accept succeeded: created name_entry=%p conn->stream_state=0x%X\n",
                                (void *)conn_record->name_entry, (unsigned int)conn->stream_state);
                        fflush(log);
                    }
                }

                return_code = OS_ObjectIdFinalizeNew(return_code, &conn_token, connsock_id);

                if (log)
                {
                    fprintf(log, "OS_ObjectIdFinalizeNew returned %d final connsock_id=%p\n", (int)return_code, (void *)connsock_id);
                    fflush(log);
                }
            }
        }

        OS_ObjectIdRelease(&sock_token);
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_SocketAccept returning %d\n", (int)return_code);
        fflush(log);
        fclose(log);
    }

    return return_code;
}
