int32 OS_SocketAccept_Impl(const OS_object_token_t *sock_token, const OS_object_token_t *conn_token,
                           OS_SockAddr_t *Addr, int32 timeout)
{
    int32                           return_code;
    uint32                          operation;
    socklen_t                       addrlen;
    OS_impl_file_internal_record_t *sock_impl;
    OS_impl_file_internal_record_t *conn_impl;

    /* Open log file for append. This log captures control flow and state
     * relevant to the pointer/const-use scenario described in the report.
     * The log is intentionally lightweight so as not to change behavior. */
    FILE *log = fopen("/tmp/logs/2585.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_SocketAccept_Impl(sock_token=%p, conn_token=%p, Addr=%p, timeout=%d)\n",
                (const void *)sock_token, (const void *)conn_token, (void *)Addr, (int)timeout);
        fflush(log);
    }

    sock_impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *sock_token);
    conn_impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *conn_token);

    if (log)
    {
        fprintf(log, "After table lookup: sock_impl=%p, conn_impl=%p\n",
                (void *)sock_impl, (void *)conn_impl);
        /* Safely print some members if pointers appear valid. */
        if (sock_impl)
        {
            fprintf(log, "  sock_impl->fd=%d, sock_impl->selectable=%d\n",
                    sock_impl->fd, (int)sock_impl->selectable);
        }
        else
        {
            fprintf(log, "  sock_impl is NULL\n");
        }
        if (conn_impl)
        {
            fprintf(log, "  conn_impl->fd(before)=%d\n", conn_impl->fd);
        }
        else
        {
            fprintf(log, "  conn_impl is NULL\n");
        }
        fflush(log);
    }

    operation = OS_STREAM_STATE_READABLE;
    if (sock_impl->selectable)
    {
        if (log)
        {
            fprintf(log, "sock_impl->selectable is true: calling OS_SelectSingle_Impl\n");
            fflush(log);
        }
        return_code = OS_SelectSingle_Impl(sock_token, &operation, timeout);
        if (log)
        {
            fprintf(log, "OS_SelectSingle_Impl returned %d, operation=0x%08x\n",
                    (int)return_code, (unsigned)operation);
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "sock_impl->selectable is false: skipping select, assuming success\n");
            fflush(log);
        }
        return_code = OS_SUCCESS;
    }

    if (return_code == OS_SUCCESS)
    {
        if ((operation & OS_STREAM_STATE_READABLE) == 0)
        {
            if (log)
            {
                fprintf(log, "No readable state after select; timing out. operation=0x%08x\n", (unsigned)operation);
                fflush(log);
            }
            return_code = OS_ERROR_TIMEOUT;
        }
        else
        {
            addrlen = Addr->ActualLength;
            if (log)
            {
                fprintf(log, "Before accept: addrlen=%lu, sock_impl->fd=%d\n",
                        (unsigned long)addrlen, sock_impl->fd);
                fflush(log);
            }

            /* Preserve original behavior: assign accepted fd into conn_impl->fd. */
            conn_impl->fd = accept(sock_impl->fd, (struct sockaddr *)&Addr->AddrData, &addrlen);

            if (log)
            {
                fprintf(log, "accept() returned fd=%d\n", conn_impl->fd);
                fflush(log);
            }

            if (conn_impl->fd < 0)
            {
                if (log)
                {
                    fprintf(log, "accept failed, setting return_code = OS_ERROR\n");
                    fflush(log);
                }
                return_code = OS_ERROR;
            }
            else
            {
                Addr->ActualLength = addrlen;

                OS_IMPL_SET_SOCKET_FLAGS(conn_token);

                if (log)
                {
                    fprintf(log, "accept succeeded: conn_impl->fd=%d, Addr->ActualLength=%lu\n",
                            conn_impl->fd, (unsigned long)Addr->ActualLength);
                    fflush(log);
                }
            }
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_SocketAccept_Impl returning %d\n\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
