int32 OS_SocketAddrFromString_Impl(OS_SockAddr_t *Addr, const char *string)
{
    void *                  addrbuffer;
    OS_SockAddr_Accessor_t *Accessor;
    int                     inet_res;
    FILE *                  log = NULL;

    /* Prepare accessor as in original implementation */
    Accessor = (OS_SockAddr_Accessor_t *)&Addr->AddrData;

    /* Open a log file to capture control flow and state for debugging. Append mode to preserve history. */
    log = fopen("/tmp/logs/410.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_SocketAddrFromString_Impl Addr=%p string=\"%s\" family=%d\n",
                (void *)Addr, (string != NULL) ? string : "(null)", (int)Accessor->sa.sa_family);
        fflush(log);
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            if (log != NULL)
            {
                fprintf(log, "PATH: AF_INET selected; addrbuffer=%p\n", addrbuffer);
                fflush(log);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            if (log != NULL)
            {
                fprintf(log, "PATH: AF_INET6 selected; addrbuffer=%p\n", addrbuffer);
                fflush(log);
            }
            break;
#endif
        default:
            if (log != NULL)
            {
                fprintf(log, "ERROR: Unsupported family %d -> returning OS_ERR_BAD_ADDRESS\n", (int)Accessor->sa.sa_family);
                fclose(log);
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    if (log != NULL)
    {
        fprintf(log, "CALL: inet_pton(family=%d, string=\"%s\", addrbuffer=%p)\n",
                (int)Accessor->sa.sa_family, (string != NULL) ? string : "(null)", addrbuffer);
        fflush(log);
    }

    /* This function is defined as returning 1 on success, not 0 */
    inet_res = inet_pton(Accessor->sa.sa_family, string, addrbuffer);

    if (log != NULL)
    {
        fprintf(log, "RESULT: inet_pton returned %d\n", inet_res);
    }

    if (inet_res != 1)
    {
        if (log != NULL)
        {
            fprintf(log, "RETURN: OS_ERROR (inet_pton failed)\n");
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log != NULL)
    {
        fprintf(log, "RETURN: OS_SUCCESS\n");
        fclose(log);
    }

    return OS_SUCCESS;
}
