int32 OS_SocketAddrGetPort_Impl(uint16 *PortNum, const OS_SockAddr_t *Addr)
{
    in_port_t                     sa_port;
    const OS_SockAddr_Accessor_t *Accessor;

    /* Open a persistent append-only log so that test harnesses can inspect control flow. */
    FILE *log = fopen("/tmp/logs/2670.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_SocketAddrGetPort_Impl(PortNum=%p, Addr=%p)\n", (void *)PortNum, (const void *)Addr);
        fflush(log);
    }

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    if (log)
    {
        /* Log the address family value observed. */
        fprintf(log, "Observed sa_family=%d\n", (int)Accessor->sa.sa_family);
        fflush(log);
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            sa_port = Accessor->sa_in.sin_port;
            if (log)
            {
                fprintf(log, "Branch: AF_INET - raw sa_port=0x%04x (%u)\n", (unsigned)sa_port, (unsigned)sa_port);
                fflush(log);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            sa_port = Accessor->sa_in6.sin6_port;
            if (log)
            {
                fprintf(log, "Branch: AF_INET6 - raw sa_port=0x%04x (%u)\n", (unsigned)sa_port, (unsigned)sa_port);
                fflush(log);
            }
            break;
#endif
        default:
            if (log)
            {
                fprintf(log, "Branch: DEFAULT - returning OS_ERR_BAD_ADDRESS\n");
                fclose(log);
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    /* Convert network byte order port to host order and preserve original behavior. */
    *PortNum = ntohs(sa_port);

    if (log)
    {
        fprintf(log, "Exit: OS_SocketAddrGetPort_Impl - converted PortNum=%u, returning OS_SUCCESS\n", (unsigned)*PortNum);
        fclose(log);
    }

    return OS_SUCCESS;
}
