int32 OS_SocketAddrGetPort_Impl(uint16 *PortNum, const OS_SockAddr_t *Addr)
{
    in_port_t                     sa_port;
    const OS_SockAddr_Accessor_t *Accessor;
    FILE *logf = NULL;

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    /* Log function entry and key state */
    logf = fopen("/tmp/logs/2676.txt", "a");
    if (logf)
    {
        fprintf(logf, "ENTER: OS_SocketAddrGetPort_Impl Addr=%p AddrData=%p sa_family=%d\n",
                (void *)Addr, (void *)&Addr->AddrData, (int)Accessor->sa.sa_family);
        fclose(logf);
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            sa_port = Accessor->sa_in.sin_port;
            /* Log which branch was taken and the raw port value (network order) */
            logf = fopen("/tmp/logs/2676.txt", "a");
            if (logf)
            {
                fprintf(logf, "BRANCH: AF_INET taken, raw sa_port(net order)=%u\n", (unsigned)sa_port);
                fclose(logf);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            sa_port = Accessor->sa_in6.sin6_port;
            /* Log which branch was taken and the raw port value (network order) */
            logf = fopen("/tmp/logs/2676.txt", "a");
            if (logf)
            {
                fprintf(logf, "BRANCH: AF_INET6 taken, raw sa_port(net order)=%u\n", (unsigned)sa_port);
                fclose(logf);
            }
            break;
#endif
        default:
            /* Log hitting the default (invalid address family) before returning */
            logf = fopen("/tmp/logs/2676.txt", "a");
            if (logf)
            {
                fprintf(logf, "BRANCH: default taken, sa_family=%d, returning OS_ERR_BAD_ADDRESS (note: unreachable 'break' after return remains)\n",
                        (int)Accessor->sa.sa_family);
                fclose(logf);
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    *PortNum = ntohs(sa_port);

    /* Log successful exit and computed port (host order) */
    logf = fopen("/tmp/logs/2676.txt", "a");
    if (logf)
    {
        fprintf(logf, "EXIT: OS_SUCCESS, PortNum=%u\n", (unsigned)*PortNum);
        fclose(logf);
    }

    return OS_SUCCESS;
}
