int32 OS_SocketAddrToString_Impl(char *buffer, size_t buflen, const OS_SockAddr_t *Addr)
{
    const void *                  addrbuffer;
    const OS_SockAddr_Accessor_t *Accessor;

    /* Minimal entry log so test harness proves the function was entered. */
    {
        FILE *log = fopen("/tmp/logs/960.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_SocketAddrToString_Impl(buffer=%p, buflen=%zu, Addr=%p)\n",
                    (void *)buffer, buflen, (const void *)Addr);
            fclose(log);
        }
    }

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            {
                FILE *log = fopen("/tmp/logs/960.txt", "a");
                if (log != NULL)
                {
                    const unsigned char *bytes = (const unsigned char *)&Accessor->sa_in.sin_addr;
                    fprintf(log, "SELECTED AF_INET, addr bytes = %u.%u.%u.%u\n",
                            (unsigned)bytes[0], (unsigned)bytes[1], (unsigned)bytes[2], (unsigned)bytes[3]);
                    fclose(log);
                }
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            {
                FILE *log = fopen("/tmp/logs/960.txt", "a");
                if (log != NULL)
                {
                    const unsigned char *bytes = (const unsigned char *)&Accessor->sa_in6.sin6_addr;
                    fprintf(log, "SELECTED AF_INET6, addr bytes =");
                    for (int i = 0; i < 16; ++i)
                    {
                        fprintf(log, " %02x", bytes[i]);
                    }
                    fprintf(log, "\n");
                    fclose(log);
                }
            }
            break;
#endif
        default:
            {
                FILE *log = fopen("/tmp/logs/960.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "ERROR: unsupported sa_family=%d -> returning OS_ERR_BAD_ADDRESS\n",
                            (int)Accessor->sa.sa_family);
                    fclose(log);
                }
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    if (inet_ntop(Accessor->sa.sa_family, addrbuffer, buffer, buflen) == NULL)
    {
        FILE *log = fopen("/tmp/logs/960.txt", "a");
        if (log != NULL)
        {
            /* Log the failure of inet_ntop; include errno if available. */
            fprintf(log, "inet_ntop FAILED for family=%d, buffer=%p, buflen=%zu -> returning OS_ERROR\n",
                    (int)Accessor->sa.sa_family, (void *)buffer, buflen);
            fclose(log);
        }

        return OS_ERROR;
    }

    {
        FILE *log = fopen("/tmp/logs/960.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT: OS_SocketAddrToString_Impl -> success, result='%s'\n", buffer);
            fclose(log);
        }
    }

    return OS_SUCCESS;
}
