int32 OS_SocketAddrToString_Impl(char *buffer, size_t buflen, const OS_SockAddr_t *Addr)
{
    const void *                  addrbuffer;
    const OS_SockAddr_Accessor_t *Accessor;
    FILE *log = fopen("/tmp/logs/966.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "ENTER OS_SocketAddrToString_Impl: buffer=%p buflen=%zu Addr=%p\n", (void *)buffer, buflen, (const void *)Addr);
    }

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    if (log != NULL)
    {
        fprintf(log, "Accessor->sa.sa_family=%d\n", Accessor->sa.sa_family);
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            if (log != NULL)
            {
                fprintf(log, "switch: AF_INET taken, addrbuffer=%p\n", addrbuffer);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            if (log != NULL)
            {
                fprintf(log, "switch: AF_INET6 taken, addrbuffer=%p\n", addrbuffer);
            }
            break;
#endif
        default:
            /* Log the default path and return the bad address error
               Keeping the 'break;' after return to preserve the original
               code structure and reproduce the unreachable code scenario. */
            if (log != NULL)
            {
                fprintf(log, "switch: default taken -> returning OS_ERR_BAD_ADDRESS (note: a 'break' follows this return and is therefore unreachable)\n");
                fclose(log);
                log = NULL;
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    if (log != NULL)
    {
        fprintf(log, "After switch: calling inet_ntop with family=%d addrbuffer=%p\n", Accessor->sa.sa_family, addrbuffer);
    }

    if (inet_ntop(Accessor->sa.sa_family, addrbuffer, buffer, buflen) == NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "inet_ntop returned NULL -> returning OS_ERROR\n");
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log != NULL)
    {
        fprintf(log, "inet_ntop succeeded -> returning OS_SUCCESS buffer=\"%s\"\n", buffer);
        fclose(log);
    }

    return OS_SUCCESS;
}
