int32 OS_SocketAddrToString_Impl(char *buffer, size_t buflen, const OS_SockAddr_t *Addr)
{
    const void *                  addrbuffer;
    const OS_SockAddr_Accessor_t *Accessor;
    FILE *log = fopen("/tmp/logs/968.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "OS_SocketAddrToString_Impl: Enter. buffer=%p buflen=%zu Addr=%p\n",
                (void *)buffer, buflen, (const void *)Addr);
    }

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    if (log != NULL)
    {
        fprintf(log, "Accessor=%p family=%d\n", (const void *)Accessor, (int)Accessor->sa.sa_family);
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            if (log != NULL)
            {
                fprintf(log, "Selected AF_INET addrbuffer=%p\n", addrbuffer);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            if (log != NULL)
            {
                fprintf(log, "Selected AF_INET6 addrbuffer=%p\n", addrbuffer);
            }
            break;
#endif
        default:
            if (log != NULL)
            {
                fprintf(log, "Bad address family: %d -> returning OS_ERR_BAD_ADDRESS\n", (int)Accessor->sa.sa_family);
                fclose(log);
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    if (log != NULL)
    {
        fprintf(log, "Calling inet_ntop for family=%d addrbuffer=%p\n", (int)Accessor->sa.sa_family, addrbuffer);
    }

    if (inet_ntop(Accessor->sa.sa_family, addrbuffer, buffer, buflen) == NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "inet_ntop returned NULL -> returning OS_ERROR\n");
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log != NULL)
    {
        /* Log the resulting string (if printable) and return status */
        fprintf(log, "inet_ntop success buffer=%p result=\"%s\" -> returning OS_SUCCESS\n", (void *)buffer, buffer);
        fclose(log);
    }

    return OS_SUCCESS;
}
