int32 OS_SocketAddrToString_Impl(char *buffer, size_t buflen, const OS_SockAddr_t *Addr)
{
    const void *                  addrbuffer;
    const OS_SockAddr_Accessor_t *Accessor;
    FILE *log = fopen("/tmp/logs/968.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER OS_SocketAddrToString_Impl: buffer=%p buflen=%zu Addr=%p\n",
                (void *)buffer, buflen, (const void *)Addr);
    }

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    /* Log the family up-front for easier control-flow tracing */
    if (log)
    {
        fprintf(log, "Address family value: %d\n", (int)Accessor->sa.sa_family);
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            if (log)
            {
                const unsigned char *bytes = (const unsigned char *)addrbuffer;
                /* Print first 4 bytes for IPv4 */
                fprintf(log, "Branch AF_INET taken. addr bytes: %u.%u.%u.%u\n",
                        (unsigned)bytes[0], (unsigned)bytes[1], (unsigned)bytes[2], (unsigned)bytes[3]);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            if (log)
            {
                const unsigned char *bytes6 = (const unsigned char *)addrbuffer;
                /* Print first 8 bytes (truncated) for IPv6 to avoid huge logs */
                fprintf(log, "Branch AF_INET6 taken. first 8 addr bytes:");
                for (int i = 0; i < 8; ++i)
                {
                    fprintf(log, " %02x", (unsigned)bytes6[i]);
                }
                fprintf(log, "\n");
            }
            break;
#endif
        default:
            if (log)
            {
                fprintf(log, "Branch DEFAULT taken. Returning OS_ERR_BAD_ADDRESS\n");
                fclose(log);
            }
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    if (inet_ntop(Accessor->sa.sa_family, addrbuffer, buffer, buflen) == NULL)
    {
        if (log)
        {
            fprintf(log, "inet_ntop returned NULL -> returning OS_ERROR\n");
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log)
    {
        /* Log the resulting string in buffer (may be large but useful for tracing) */
        fprintf(log, "inet_ntop succeeded. result: %s\n", buffer ? buffer : "(null)");
        fprintf(log, "EXIT OS_SocketAddrToString_Impl -> OS_SUCCESS\n");
        fclose(log);
    }

    return OS_SUCCESS;
}
