int32 OS_SocketBindAddress(osal_id_t sock_id, const OS_SockAddr_t *Addr)
{
    OS_common_record_t *         record;
    OS_stream_internal_record_t *stream;
    OS_object_token_t            token;
    int32                        return_code;

    /* Log entry and parameters */
    {
        FILE *logf = fopen("/tmp/logs/2318.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTER OS_SocketBindAddress: sock_id=%u Addr=%p\n", (unsigned int)sock_id, (const void *)Addr);
            fflush(logf);
            fclose(logf);
        }
    }

    /* Check Parameters */
    OS_CHECK_POINTER(Addr);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, sock_id, &token);

    /* Log result of GetById */
    {
        FILE *logf = fopen("/tmp/logs/2318.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdGetById returned %d for sock_id=%u token_addr=%p\n", (int)return_code, (unsigned int)sock_id, (void *)&token);
            fflush(logf);
            fclose(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);

        /* Log record/stream state */
        {
            FILE *logf = fopen("/tmp/logs/2318.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "record=%p stream=%p socket_domain=%u stream_state=0x%08x\n",
                        (void *)record, (void *)stream,
                        (unsigned int)stream->socket_domain,
                        (unsigned int)stream->stream_state);
                fflush(logf);
                fclose(logf);
            }
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID)
        {
            /* Not a socket */
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;

            FILE *logf = fopen("/tmp/logs/2318.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Path: Not a socket. Setting return_code=%d\n", (int)return_code);
                fflush(logf);
                fclose(logf);
            }
        }
        else if ((stream->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != 0)
        {
            /* Socket must be neither bound nor connected */
            return_code = OS_ERR_INCORRECT_OBJ_STATE;

            FILE *logf = fopen("/tmp/logs/2318.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Path: Incorrect state (stream_state=0x%08x). Setting return_code=%d\n", (unsigned int)stream->stream_state, (int)return_code);
                fflush(logf);
                fclose(logf);
            }
        }
        else
        {
            /* Call implementation and log its result */
            {
                FILE *logf = fopen("/tmp/logs/2318.txt", "a");
                if (logf != NULL)
                {
                    fprintf(logf, "Calling OS_SocketBindAddress_Impl with token_addr=%p Addr=%p\n", (void *)&token, (const void *)Addr);
                    fflush(logf);
                    fclose(logf);
                }
            }

            return_code = OS_SocketBindAddress_Impl(&token, Addr);

            {
                FILE *logf = fopen("/tmp/logs/2318.txt", "a");
                if (logf != NULL)
                {
                    fprintf(logf, "OS_SocketBindAddress_Impl returned %d\n", (int)return_code);
                    fflush(logf);
                    fclose(logf);
                }
            }

            if (return_code == OS_SUCCESS)
            {
                OS_CreateSocketName(&token, Addr, NULL);
                record->name_entry = stream->stream_name;
                stream->stream_state |= OS_STREAM_STATE_BOUND;

                FILE *logf = fopen("/tmp/logs/2318.txt", "a");
                if (logf != NULL)
                {
                    fprintf(logf, "Bind success: Created name_entry=%p stream_name=%p new_stream_state=0x%08x\n",
                            (void *)record->name_entry, (void *)stream->stream_name, (unsigned int)stream->stream_state);
                    fflush(logf);
                    fclose(logf);
                }
            }
        }

        OS_ObjectIdRelease(&token);

        {
            FILE *logf = fopen("/tmp/logs/2318.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Released token at %p; final return_code=%d\n", (void *)&token, (int)return_code);
                fflush(logf);
                fclose(logf);
            }
        }
    }

    /* Final log before returning */
    {
        FILE *logf = fopen("/tmp/logs/2318.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "EXIT OS_SocketBindAddress: return_code=%d\n", (int)return_code);
            fflush(logf);
            fclose(logf);
        }
    }

    return return_code;
}
