int32 OS_SocketBindAddress(osal_id_t sock_id, const OS_SockAddr_t *Addr)
{
    OS_common_record_t *         record;
    OS_stream_internal_record_t *stream;
    OS_object_token_t            token;
    int32                        return_code;

    /* Open log for tracing control flow and state for MISRA Rule 2.2 investigation */
    FILE *log = fopen("/tmp/logs/2318.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_SocketBindAddress\n");
        fprintf(log, "  sock_id=%lu, Addr=%p\n", (unsigned long)sock_id, (void *)Addr);
        fflush(log);
    }

    /* Check Parameters */
    if (log)
    {
        fprintf(log, "  About to perform OS_CHECK_POINTER(Addr)\n");
        fflush(log);
    }

    OS_CHECK_POINTER(Addr);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, sock_id, &token);

    if (log)
    {
        fprintf(log, "  After OS_ObjectIdGetById: return_code=%ld, token=%lu\n", (long)return_code, (unsigned long)token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);

        if (log)
        {
            fprintf(log, "  record=%p, stream=%p\n", (void *)record, (void *)stream);
            if (stream)
            {
                fprintf(log, "  stream->socket_domain=%d, stream->stream_state=0x%08lx, stream->stream_name=%s\n",
                        (int)stream->socket_domain, (unsigned long)stream->stream_state,
                        (stream->stream_name != NULL) ? stream->stream_name : "(null)");
            }
            fflush(log);
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID)
        {
            /* Not a socket */
            if (log)
            {
                fprintf(log, "  Condition: socket_domain == OS_SocketDomain_INVALID -> OS_ERR_INCORRECT_OBJ_TYPE\n");
                fflush(log);
            }
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
        }
        else if ((stream->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != 0)
        {
            /* Socket must be neither bound nor connected */
            if (log)
            {
                fprintf(log, "  Condition: already BOUND or CONNECTED (stream_state=0x%08lx) -> OS_ERR_INCORRECT_OBJ_STATE\n",
                        (unsigned long)stream->stream_state);
                fflush(log);
            }
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            if (log)
            {
                fprintf(log, "  Calling OS_SocketBindAddress_Impl with token=%lu Addr=%p\n", (unsigned long)token, (const void *)Addr);
                fflush(log);
            }

            return_code = OS_SocketBindAddress_Impl(&token, Addr);

            if (log)
            {
                fprintf(log, "  After OS_SocketBindAddress_Impl: return_code=%ld\n", (long)return_code);
                fflush(log);
            }

            if (return_code == OS_SUCCESS)
            {
                OS_CreateSocketName(&token, Addr, NULL);

                if (log)
                {
                    fprintf(log, "  OS_CreateSocketName called. stream->stream_name=%s\n",
                            (stream && stream->stream_name) ? stream->stream_name : "(null)");
                    fflush(log);
                }

                record->name_entry = stream->stream_name;
                stream->stream_state |= OS_STREAM_STATE_BOUND;

                if (log)
                {
                    fprintf(log, "  Bound: record->name_entry=%s, new stream_state=0x%08lx\n",
                            (record && record->name_entry) ? record->name_entry : "(null)",
                            (unsigned long)stream->stream_state);
                    fflush(log);
                }
            }
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "  Released token. Exiting with return_code=%ld\n", (long)return_code);
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "  OS_ObjectIdGetById failed -> exiting with return_code=%ld\n", (long)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_SocketBindAddress return %ld\n\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
