int32 OS_SocketBindAddress(osal_id_t sock_id, const OS_SockAddr_t *Addr)
{
    OS_common_record_t *         record;
    OS_stream_internal_record_t *stream;
    OS_object_token_t            token;
    int32                        return_code;
    FILE *logf = NULL;

    /* Minimal entry log so runs always produce output */
    logf = fopen("/tmp/logs/2315.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "ENTER: OS_SocketBindAddress(sock_id=%u, Addr=%p)\n", (unsigned)sock_id, (void *)Addr);
        fflush(logf);
    }

    /* Check Parameters */
    OS_CHECK_POINTER(Addr);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, sock_id, &token);
    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);

        if (logf == NULL)
        {
            /* Try to open on-demand if initial open failed */
            logf = fopen("/tmp/logs/2315.txt", "a");
        }

        if (logf != NULL)
        {
            fprintf(logf, "token=%p, record=%p, stream=%p\n", (void *)&token, (void *)record, (void *)stream);
            fprintf(logf, "stream->socket_domain=%d, stream->stream_state=0x%08x\n",
                    (int)stream->socket_domain, (unsigned)stream->stream_state);
            /* Log pointer values for name fields before any assignment */
            fprintf(logf, "BEFORE_ASSIGN: record->name_entry=%p, stream->stream_name=%p\n",
                    (void *)record->name_entry, (void *)stream->stream_name);
            if (stream->stream_name != NULL)
            {
                /* Print the string contents if it looks valid (best-effort) */
                fprintf(logf, "stream->stream_name (as string)='%s'\n", stream->stream_name);
            }
            if (record->name_entry != NULL)
            {
                fprintf(logf, "record->name_entry (as string)='%s'\n", record->name_entry);
            }
            fflush(logf);
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID)
        {
            /* Not a socket */
            if (logf != NULL)
            {
                fprintf(logf, "BRANCH: Not a socket -> OS_ERR_INCORRECT_OBJ_TYPE\n");
                fflush(logf);
            }

            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
        }
        else if ((stream->stream_state & (OS_STREAM_STATE_BOUND | OS_STREAM_STATE_CONNECTED)) != 0)
        {
            /* Socket must be neither bound nor connected */
            if (logf != NULL)
            {
                fprintf(logf, "BRANCH: Incorrect state (already bound or connected) -> OS_ERR_INCORRECT_OBJ_STATE\n");
                fflush(logf);
            }

            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            return_code = OS_SocketBindAddress_Impl(&token, Addr);

            if (return_code == OS_SUCCESS)
            {
                OS_CreateSocketName(&token, Addr, NULL);

                /* Log right before the potentially-qualifier-discarding assignment */
                if (logf != NULL)
                {
                    fprintf(logf, "ASSIGNING: record->name_entry <- stream->stream_name\n");
                    fprintf(logf, "ASSIGN_PRE: record->name_entry=%p, stream->stream_name=%p\n",
                            (void *)record->name_entry, (void *)stream->stream_name);
                    fflush(logf);
                }

                record->name_entry = stream->stream_name;

                if (logf != NULL)
                {
                    fprintf(logf, "ASSIGN_POST: record->name_entry=%p (should equal stream->stream_name=%p)\n",
                            (void *)record->name_entry, (void *)stream->stream_name);
                    if (record->name_entry != NULL)
                    {
                        fprintf(logf, "ASSIGN_POST_STR: record->name_entry (as string)='%s'\n", record->name_entry);
                    }
                    fflush(logf);
                }

                stream->stream_state |= OS_STREAM_STATE_BOUND;

                if (logf != NULL)
                {
                    fprintf(logf, "STATE_UPDATE: stream->stream_state=0x%08x\n", (unsigned)stream->stream_state);
                    fflush(logf);
                }
            }
            else
            {
                if (logf != NULL)
                {
                    fprintf(logf, "IMPL_FAILED: OS_SocketBindAddress_Impl returned %d\n", (int)return_code);
                    fflush(logf);
                }
            }
        }

        OS_ObjectIdRelease(&token);

        if (logf != NULL)
        {
            fprintf(logf, "EXIT_BEFORE_RETURN: return_code=%d\n", (int)return_code);
            fflush(logf);
            fclose(logf);
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "EXIT_IMMEDIATE: OS_ObjectIdGetById returned %d\n", (int)return_code);
            fflush(logf);
            fclose(logf);
        }
    }

    return return_code;
}
