int32 OS_SocketGetInfo(osal_id_t sock_id, OS_socket_prop_t *sock_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;

    FILE *log = fopen("/tmp/logs/835.txt", "a");
    if (log)
    {
        fprintf(log, "OS_SocketGetInfo ENTRY: sock_id=%lu sock_prop=%p\n", (unsigned long)sock_id, (void *)sock_prop);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(sock_prop);

    memset(sock_prop, 0, sizeof(OS_socket_prop_t));
    if (log)
    {
        fprintf(log, "memset(sock_prop) done\n");
        fflush(log);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, sock_id, &token);
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %ld, token@%p\n", (long)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);

        if (log)
        {
            fprintf(log, "OS_OBJECT_TABLE_GET returned record@%p\n", (void *)record);
            if (record)
            {
                /* Log the fields used by the function to demonstrate the violation scenario */
                fprintf(log, "record->name_entry=\"%s\"\n", record->name_entry);
                fprintf(log, "record->creator=%lu\n", (unsigned long)record->creator);
            }
            fflush(log);
        }

        strncpy(sock_prop->name, record->name_entry, sizeof(sock_prop->name) - 1);
        sock_prop->creator = record->creator;

        if (log)
        {
            fprintf(log, "Assigned sock_prop->name and sock_prop->creator from record\n");
            fflush(log);
        }

        return_code        = OS_SocketGetInfo_Impl(&token, sock_prop);

        if (log)
        {
            fprintf(log, "OS_SocketGetInfo_Impl returned %ld\n", (long)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "OS_ObjectIdRelease(&token) called\n");
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_SocketGetInfo EXIT: return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
