int32 OS_SocketListen_Impl(const OS_object_token_t *token)
{
    int                             os_result;
    OS_impl_file_internal_record_t *impl;

    /* Open log file (primary target) to record control flow and state. If that fails,
       fall back to a file in /tmp to increase chances of capturing logs. */
    FILE *logf = fopen("/tmp/logs/3108.txt", "a");
    if (logf == NULL)
    {
        logf = fopen("/tmp/3108.txt", "a");
    }

    if (logf)
    {
        fprintf(logf, "OS_SocketListen_Impl: enter token=%p\n", (const void *)token);
        fflush(logf);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (logf)
    {
        /* Avoid dereferencing impl if NULL when printing; print fd only when impl is non-NULL. */
        if (impl != NULL)
        {
            fprintf(logf, "OS_SocketListen_Impl: impl=%p, fd=%d\n", (void *)impl, impl->fd);
        }
        else
        {
            fprintf(logf, "OS_SocketListen_Impl: impl=NULL\n");
        }
        fflush(logf);
    }

    os_result = listen(impl->fd, 10);
    if (os_result < 0)
    {
        int saved_errno = errno;
        if (logf)
        {
            fprintf(logf, "OS_SocketListen_Impl: listen returned %d, errno=%d (%s)\n",
                    os_result, saved_errno, strerror(saved_errno));
            fclose(logf);
        }
        else
        {
            /* Ensure some observable output even if the file could not be opened. */
            fprintf(stderr, "OS_SocketListen_Impl: listen returned %d, errno=%d (%s)\n",
                    os_result, saved_errno, strerror(saved_errno));
        }
        return OS_ERROR;
    }

    if (logf)
    {
        fprintf(logf, "OS_SocketListen_Impl: listen succeeded, os_result=%d\n", os_result);
        fclose(logf);
    }

    return OS_SUCCESS;
}
