int32 OS_SocketOpen(osal_id_t *sock_id, OS_SocketDomain_t Domain, OS_SocketType_t Type)
{
    OS_object_token_t            token;
    OS_stream_internal_record_t *stream;
    int32                        return_code;
    FILE                        *log = NULL;

    /* Check for NULL pointers */
    OS_CHECK_POINTER(sock_id);

    /* Open log for appending control-flow and state information */
    log = fopen("/tmp/logs/2640.txt", "a");
    if (log)
    {
        fprintf(log, "OS_SocketOpen ENTER: sock_id_ptr=%p Domain=%d Type=%d\n",
                (void *)sock_id, (int)Domain, (int)Type);
        fflush(log);
    }

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    if (log)
    {
        fprintf(log, "About to call OS_ObjectIdAllocateNew\n");
        fflush(log);
    }
    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, NULL, &token);
    if (log)
    {
        fprintf(log, "Returned from OS_ObjectIdAllocateNew: return_code=%ld token_addr=%p\n",
                (long)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);

        if (log)
        {
            fprintf(log, "Allocated stream record: stream_ptr=%p\n", (void *)stream);
            fflush(log);
        }

        /* Save all the data to our own internal table */
        memset(stream, 0, sizeof(OS_stream_internal_record_t));
        stream->socket_domain = Domain;
        stream->socket_type   = Type;

        if (log)
        {
            fprintf(log, "Initialized stream record: socket_domain=%d socket_type=%d\n",
                    (int)stream->socket_domain, (int)stream->socket_type);
            fflush(log);
        }

        /* Now call the OS-specific implementation.  This reads info from the table. */
        if (log)
        {
            fprintf(log, "About to call OS_SocketOpen_Impl with token_addr=%p\n", (void *)&token);
            fflush(log);
        }
        return_code = OS_SocketOpen_Impl(&token);

        if (log)
        {
            fprintf(log, "Returned from OS_SocketOpen_Impl: return_code=%ld\n", (long)return_code);
            fflush(log);
        }

        /* Check result, finalize record, and unlock global table. */
        if (log)
        {
            fprintf(log, "About to call OS_ObjectIdFinalizeNew(return_code=%ld, token_addr=%p, sock_id_ptr=%p)\n",
                    (long)return_code, (void *)&token, (void *)sock_id);
            fflush(log);
        }
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, sock_id);

        if (log)
        {
            fprintf(log, "Returned from OS_ObjectIdFinalizeNew: final_return_code=%ld\n", (long)return_code);
            if ((return_code == OS_SUCCESS) && (sock_id != NULL))
            {
                fprintf(log, "Final socket id stored at %p -> %lld\n", (void *)sock_id, (long long)*sock_id);
            }
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_SocketOpen EXIT: return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
