int32 OS_SocketOpen_Impl(const OS_object_token_t *token)
{
    int                             os_domain;
    int                             os_type;
    int                             os_proto;
    int                             os_flags;
    OS_impl_file_internal_record_t *impl;
    OS_stream_internal_record_t *   stream;

    impl   = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);
    stream = OS_OBJECT_TABLE_GET(OS_stream_table, *token);

    /* Open log file for tracing control flow and state for rule-2.2 investigation. */
    FILE *log = fopen("/tmp/logs/1608.txt", "a");
    if (log)
    {
        fprintf(log, "OS_SocketOpen_Impl: ENTRY token=%p\n", (const void *)token);
    }

    os_proto = 0;
    if (log)
    {
        fprintf(log, "OS_SocketOpen_Impl: initial os_proto=%d\n", os_proto);
    }

    switch (stream->socket_type)
    {
        case OS_SocketType_DATAGRAM:
            os_type  = SOCK_DGRAM;
            os_proto = IPPROTO_UDP;
            if (log) fprintf(log, "socket_type=DATAGRAM -> os_type=%d os_proto=%d\n", os_type, os_proto);
            break;

        case OS_SocketType_STREAM:
            os_type  = SOCK_STREAM;
            os_proto = IPPROTO_TCP;
            if (log) fprintf(log, "socket_type=STREAM -> os_type=%d os_proto=%d\n", os_type, os_proto);
            break;

        default:
            if (log)
            {
                fprintf(log, "socket_type=DEFAULT -> returning OS_ERR_NOT_IMPLEMENTED\n");
                fclose(log);
            }
            return OS_ERR_NOT_IMPLEMENTED;
    }

    switch (stream->socket_domain)
    {
        case OS_SocketDomain_INET:
            os_domain = AF_INET;
            if (log) fprintf(log, "socket_domain=INET -> os_domain=%d\n", os_domain);
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case OS_SocketDomain_INET6:
            os_domain = AF_INET6;
            if (log) fprintf(log, "socket_domain=INET6 -> os_domain=%d\n", os_domain);
            break;
#endif
        default:
            if (log)
            {
                fprintf(log, "socket_domain=DEFAULT -> returning OS_ERR_NOT_IMPLEMENTED\n");
                fclose(log);
            }
            return OS_ERR_NOT_IMPLEMENTED;
    }

    if (log)
    {
        fprintf(log, "About to call socket(os_domain=%d, os_type=%d, os_proto=%d)\n", os_domain, os_type, os_proto);
    }

    impl->fd = socket(os_domain, os_type, os_proto);
    if (impl->fd < 0)
    {
        if (log)
        {
            fprintf(log, "socket() FAILED -> fd=%d errno=%d\n", impl->fd, errno);
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log)
    {
        fprintf(log, "socket() SUCCEEDED -> fd=%d\n", impl->fd);
    }

    /*
     * Setting the REUSEADDR flag helps during debugging when there might be frequent
     * code restarts.  However if setting the option fails then it is not worth bailing out over.
     */
    os_flags = 1;
    setsockopt(impl->fd, SOL_SOCKET, SO_REUSEADDR, &os_flags, sizeof(os_flags));

    /*
     * Set the standard options on the filehandle by default --
     * this may set it to non-blocking mode if the implementation supports it.
     * any blocking would be done explicitly via the select() wrappers
     *
     * NOTE: The implementation still generally works without this flag set, but
     * nonblock mode does improve robustness in the event that multiple tasks
     * attempt to accept new connections from the same server socket at the same time.
     */
    OS_IMPL_SET_SOCKET_FLAGS(token);

    if (log)
    {
        fprintf(log, "OS_SocketOpen_Impl: RETURN OS_SUCCESS\n");
        fclose(log);
    }

    return OS_SUCCESS;
}
