int32 OS_SocketOpen_Impl(const OS_object_token_t *token)
{
    int                             os_domain;
    int                             os_type;
    int                             os_proto;
    int                             os_flags;
    OS_impl_file_internal_record_t *impl;
    OS_stream_internal_record_t *   stream;

    /* Open log file (ensure parent directory exists if necessary) */
    FILE *log = fopen("/tmp/logs/1605.txt", "a");
    if (log == NULL)
    {
        /* Try to create the directory and reopen (best-effort for logging) */
        (void)system("mkdir -p /tmp/logs");
        log = fopen("/tmp/logs/1605.txt", "a");
    }

    if (log)
    {
        fprintf(log, "OS_SocketOpen_Impl: entered (token=%p)\n", (const void *)token);
        fflush(log);
    }

    impl   = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);
    stream = OS_OBJECT_TABLE_GET(OS_stream_table, *token);

    if (log)
    {
        fprintf(log, "  impl=%p, stream=%p\n", (const void *)impl, (const void *)stream);
        if (stream)
        {
            fprintf(log, "  stream->socket_type=%d, stream->socket_domain=%d\n",
                    (int)stream->socket_type, (int)stream->socket_domain);
        }
        fflush(log);
    }

    os_proto = 0;

    switch (stream->socket_type)
    {
        case OS_SocketType_DATAGRAM:
            os_type  = SOCK_DGRAM;
            os_proto = IPPROTO_UDP;
            if (log)
            {
                fprintf(log, "  selected DATAGRAM: os_type=%d, os_proto=%d\n", os_type, os_proto);
                fflush(log);
            }
            break;

        case OS_SocketType_STREAM:
            os_type  = SOCK_STREAM;
            os_proto = IPPROTO_TCP;
            if (log)
            {
                fprintf(log, "  selected STREAM: os_type=%d, os_proto=%d\n", os_type, os_proto);
                fflush(log);
            }
            break;

        default:
            if (log)
            {
                fprintf(log, "  unsupported socket_type=%d -> returning OS_ERR_NOT_IMPLEMENTED\n",
                        (int)stream->socket_type);
                fflush(log);
                fclose(log);
            }
            return OS_ERR_NOT_IMPLEMENTED;
    }

    switch (stream->socket_domain)
    {
        case OS_SocketDomain_INET:
            os_domain = AF_INET;
            if (log)
            {
                fprintf(log, "  selected domain INET -> os_domain=%d\n", os_domain);
                fflush(log);
            }
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case OS_SocketDomain_INET6:
            os_domain = AF_INET6;
            if (log)
            {
                fprintf(log, "  selected domain INET6 -> os_domain=%d\n", os_domain);
                fflush(log);
            }
            break;
#endif
        default:
            if (log)
            {
                fprintf(log, "  unsupported socket_domain=%d -> returning OS_ERR_NOT_IMPLEMENTED\n",
                        (int)stream->socket_domain);
                fflush(log);
                fclose(log);
            }
            return OS_ERR_NOT_IMPLEMENTED;
    }

    impl->fd = socket(os_domain, os_type, os_proto);
    if (log)
    {
        fprintf(log, "  socket() -> fd=%d\n", impl->fd);
        fflush(log);
    }

    if (impl->fd < 0)
    {
        if (log)
        {
            int errnum = errno;
            fprintf(log, "  socket() failed: fd=%d, errno=%d (%s) -> returning OS_ERROR\n",
                    impl->fd, errnum, strerror(errnum));
            fflush(log);
            fclose(log);
        }
        return OS_ERROR;
    }

    /*
     * Setting the REUSEADDR flag helps during debugging when there might be frequent
     * code restarts.  However if setting the option fails then it is not worth bailing out over.
     */
    os_flags = 1; /* Intentionally non-const to reproduce the reported rule 8.13 scenario */

    if (log)
    {
        fprintf(log, "  os_flags set to %d at address %p (size=%zu)\n",
                os_flags, (void *)&os_flags, sizeof(os_flags));
        fflush(log);
    }

    /* Capture result of setsockopt for logging but do not change original behavior */
    {
        int setsockopt_ret = setsockopt(impl->fd, SOL_SOCKET, SO_REUSEADDR, &os_flags, sizeof(os_flags));
        if (log)
        {
            if (setsockopt_ret == 0)
            {
                fprintf(log, "  setsockopt(SO_REUSEADDR) succeeded (ret=%d)\n", setsockopt_ret);
            }
            else
            {
                int errnum = errno;
                fprintf(log, "  setsockopt(SO_REUSEADDR) failed (ret=%d, errno=%d, %s)\n",
                        setsockopt_ret, errnum, strerror(errnum));
            }
            fflush(log);
        }
    }

    /*
     * Set the standard options on the filehandle by default --
     * this may set it to non-blocking mode if the implementation supports it.
     * any blocking would be done explicitly via the select() wrappers
     *
     * NOTE: The implementation still generally works without this flag set, but
     * nonblock mode does improve robustness in the event that multiple tasks
     * attempt to accept new connections from the same server socket at the same time.
     */
    if (log)
    {
        fprintf(log, "  calling OS_IMPL_SET_SOCKET_FLAGS\n");
        fflush(log);
    }

    OS_IMPL_SET_SOCKET_FLAGS(token);

    if (log)
    {
        fprintf(log, "  OS_SocketOpen_Impl: returning OS_SUCCESS\n");
        fflush(log);
        fclose(log);
    }

    return OS_SUCCESS;
}
