int32 OS_SocketSendTo(osal_id_t sock_id, const void *buffer, size_t buflen, const OS_SockAddr_t *RemoteAddr)
{
    OS_stream_internal_record_t *stream;
    OS_object_token_t            token;
    int32                        return_code;
    FILE *logf = NULL;

    /* Open log file for append so repeated runs accumulate evidence */
    logf = fopen("/tmp/logs/2415.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "OS_SocketSendTo: ENTRY sock_id=%lu buffer=%p buflen=%zu RemoteAddr=%p\n",
                (unsigned long)sock_id, buffer, buflen, (const void *)RemoteAddr);
        fflush(logf);
    }

    /* Check Parameters */
    if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: Performing parameter checks\n"); fflush(logf); }
    OS_CHECK_POINTER(buffer);
    OS_CHECK_SIZE(buflen);
    OS_CHECK_POINTER(RemoteAddr);

    if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: Parameter checks passed\n"); fflush(logf); }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, sock_id, &token);
    if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: OS_ObjectIdGetById returned %ld\n", (long)return_code); fflush(logf); }

    if (return_code == OS_SUCCESS)
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);
        if (logf != NULL)
        {
            fprintf(logf, "OS_SocketSendTo: stream ptr=%p\n", (const void *)stream);
            /* Attempt to log the socket_type; this demonstrates read-only usage in this function */
            fprintf(logf, "OS_SocketSendTo: stream->socket_type=%d\n", (int)stream->socket_type);
            fflush(logf);
        }

        if (stream->socket_type != OS_SocketType_DATAGRAM)
        {
            if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: incorrect object type (not DATAGRAM)\n"); fflush(logf); }
            return_code = OS_ERR_INCORRECT_OBJ_TYPE;
        }
        else
        {
            if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: calling OS_SocketSendTo_Impl\n"); fflush(logf); }
            return_code = OS_SocketSendTo_Impl(&token, buffer, buflen, RemoteAddr);
            if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: OS_SocketSendTo_Impl returned %ld\n", (long)return_code); fflush(logf); }
        }

        OS_ObjectIdRelease(&token);
        if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: Released object token\n"); fflush(logf); }
    }
    else
    {
        if (logf != NULL) { fprintf(logf, "OS_SocketSendTo: Skipping stream handling due to prior error\n"); fflush(logf); }
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_SocketSendTo: EXIT return_code=%ld\n\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
