int32 OS_SocketShutdown_Impl(const OS_object_token_t *token, OS_SocketShutdownMode_t Mode)
{
    OS_impl_file_internal_record_t *conn_impl;
    int32                           return_code;
    int                             how;

    /* Logging probes: append human-readable trace to a file so other agents
     * can reproduce and observe the control flow and state that demonstrates
     * the reported Rule 8.13 scenario (the local pointer is only read).
     * The code attempts to write to /tmp/logs/2035.txt, and falls back to
     * /tmp/2035.txt if the directory does not exist. */
    FILE *log = NULL;
    const char *primary_log = "/tmp/logs/2035.txt";
    const char *fallback_log  = "/tmp/2035.txt";

    /* Note: do not alter program logic. Only open the log file for appending. */
    log = fopen(primary_log, "a");
    if (log == NULL)
    {
        log = fopen(fallback_log, "a");
    }

    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_SocketShutdown_Impl token=%p Mode=%d\n", (void *)token, (int)Mode);
    }

    conn_impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Note that when called via the shared layer,
     * the "Mode" arg has already been checked/validated. */
    if (Mode == OS_SocketShutdownMode_SHUT_READ)
    {
        how = SHUT_RD;
        if (log) fprintf(log, "Mode -> SHUT_READ (how=%d)\n", how);
    }
    else if (Mode == OS_SocketShutdownMode_SHUT_WRITE)
    {
        how = SHUT_WR;
        if (log) fprintf(log, "Mode -> SHUT_WRITE (how=%d)\n", how);
    }
    else
    {
        how = SHUT_RDWR;
        if (log) fprintf(log, "Mode -> SHUT_RDWR (how=%d)\n", how);
    }

    /* Probe the fd value immediately before invoking shutdown. This mirrors
     * the original access pattern (conn_impl->fd) so the logging does not
     * change the program's behavior other than recording state. */
    if (log)
    {
        if (conn_impl != NULL)
        {
            /* Record the pointer and the fd field. This demonstrates that the
             * local pointer variable is being read (not written) in this
             * function. */
            fprintf(log, "conn_impl=%p conn_impl->fd=%d\n", (void *)conn_impl, (int)conn_impl->fd);
        }
        else
        {
            fprintf(log, "conn_impl=NULL (will attempt to use conn_impl->fd as original code)\n");
        }
    }

    if (shutdown(conn_impl->fd, how) == 0)
    {
        return_code = OS_SUCCESS;
        if (log) fprintf(log, "shutdown(fd=%d, how=%d) -> SUCCESS return_code=%d\n", (int)conn_impl->fd, how, (int)return_code);
    }
    else
    {
        return_code = OS_ERROR;
        if (log) fprintf(log, "shutdown(fd=%d, how=%d) -> FAILURE return_code=%d errno=%d\n", (int)conn_impl->fd, how, (int)return_code, errno);
    }

    /* Re-read the fd field after shutdown to show it was not modified by this
     * function (evidence that the pointer was used read-only). This is a
     * runtime check that complements the static observation. If conn_impl is
     * NULL this will behave the same as the original code (possible crash). */
    if (log)
    {
        if (conn_impl != NULL)
        {
            fprintf(log, "post-check: conn_impl->fd=%d (unchanged by this function)\n", (int)conn_impl->fd);
        }
        fclose(log);
    }

    return return_code;
}
