int32 OS_SymbolLookup(cpuaddr *SymbolAddress, const char *SymbolName)
{
    int32 return_code;
    int32 staticsym_status;
    FILE *log = NULL;

    /*
    ** Check parameters (keep original behavior)
    */
    OS_CHECK_POINTER(SymbolAddress);
    OS_CHECK_POINTER(SymbolName);

    /* Attempt to open log file for appending. If the directory does not exist
     * fopen may fail; in that case logging will be skipped but normal behavior
     * of the function is preserved. */
    log = fopen("/tmp/logs/70.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup ENTRY: SymbolAddress_ptr=%p, SymbolName=\"%s\"\n",
                (void *)SymbolAddress, SymbolName);
        fflush(log);
    }

    /*
     * attempt to find the symbol in the symbol table
     */
    return_code = OS_SymbolLookup_Impl(SymbolAddress, SymbolName);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup: OS_SymbolLookup_Impl returned %d\n", (int)return_code);
        /* Attempt to show the stored symbol address value if it's reasonable to print.
         * We print the pointer of the user-provided location always; the numeric value
         * stored there may or may not be meaningful depending on return_code. */
        fprintf(log, "OS_SymbolLookup: SymbolAddress_ptr=%p\n", (void *)SymbolAddress);
        fflush(log);
    }

    /*
     * If the OS call did not find the symbol or the loader is
     * disabled, then check if a static symbol table is present
     */
    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "OS_SymbolLookup: Primary lookup failed (code=%d). Trying static lookup.\n", (int)return_code);
            fflush(log);
        }

        staticsym_status = OS_SymbolLookup_Static(SymbolAddress, SymbolName, NULL);

        if (log != NULL)
        {
            fprintf(log, "OS_SymbolLookup: OS_SymbolLookup_Static returned %d\n", (int)staticsym_status);
            /* If static lookup succeeded, note that we will overwrite the return code. */
            if (staticsym_status == OS_SUCCESS)
            {
                fprintf(log, "OS_SymbolLookup: Static lookup succeeded. Overwriting return_code with %d\n", (int)staticsym_status);
            }
            else
            {
                fprintf(log, "OS_SymbolLookup: Static lookup did not succeed; keeping original return_code=%d\n", (int)return_code);
            }
            fflush(log);
        }

        /*
         * Only overwrite the return code if static lookup was successful.
         * Otherwise keep the error code from the low level implementation.
         */
        if (staticsym_status == OS_SUCCESS)
        {
            return_code = staticsym_status;
        }
    }

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup EXIT: final_return_code=%d, SymbolAddress_ptr=%p\n",
                (int)return_code, (void *)SymbolAddress);
        fclose(log);
    }

    return return_code;
}
