int32 OS_SymbolLookup(cpuaddr *SymbolAddress, const char *SymbolName)
{
    int32 return_code;
    int32 staticsym_status;
    FILE *log = fopen("/tmp/logs/73.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup ENTRY: SymbolAddress=%p, SymbolName=%p", (void *)SymbolAddress, (const void *)SymbolName);
        if (SymbolName != NULL)
        {
            /* Print the pointed-to string for convenience (only if non-NULL). */
            fprintf(log, ", SymbolName=\"%s\"", SymbolName);
        }
        fprintf(log, "\n");
        fflush(log);
    }

    /*
    ** Check parameters (preserve original behavior)
    */
    OS_CHECK_POINTER(SymbolAddress);
    OS_CHECK_POINTER(SymbolName);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup: Parameters validated (SymbolAddress=%p, SymbolName=%p)\n",
                (void *)SymbolAddress, (const void *)SymbolName);
        fflush(log);
    }

    /*
     * attempt to find the symbol in the symbol table
     */
    return_code = OS_SymbolLookup_Impl(SymbolAddress, SymbolName);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup_Impl returned %d\n", return_code);
        fflush(log);
    }

    /*
     * If the OS call did not find the symbol or the loader is
     * disabled, then check if a static symbol table is present
     */
    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            /* Log that we will call the static lookup and the ModuleName being passed (NULL here) */
            fprintf(log, "OS_SymbolLookup: Falling back to static lookup. Calling OS_SymbolLookup_Static(SymbolAddress=%p, SymbolName=%p, ModuleName=%p)\n",
                    (void *)SymbolAddress, (const void *)SymbolName, (const void *)NULL);
            fflush(log);
        }

        staticsym_status = OS_SymbolLookup_Static(SymbolAddress, SymbolName, NULL);

        if (log != NULL)
        {
            fprintf(log, "OS_SymbolLookup_Static returned %d\n", staticsym_status);
            fflush(log);
        }

        /*
         * Only overwrite the return code if static lookup was successful.
         * Otherwise keep the error code from the low level implementation.
         */
        if (staticsym_status == OS_SUCCESS)
        {
            if (log != NULL)
            {
                fprintf(log, "OS_SymbolLookup: static lookup succeeded; overwriting return_code (%d -> %d)\n",
                        return_code, staticsym_status);
                fflush(log);
            }

            return_code = staticsym_status;
        }
    }

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolLookup EXIT: return_code=%d\n", return_code);
        fclose(log);
    }

    return return_code;
}
