int32 OS_SymbolLookup_Static(cpuaddr *SymbolAddress, const char *SymbolName, const char *ModuleName)
{
    int32                      return_code = OS_ERR_NOT_IMPLEMENTED;
    OS_static_symbol_record_t *StaticSym   = OS_STATIC_SYMTABLE_SOURCE;

    /* Open log file for append. This records control flow and state so other
     * agents can reproduce and see evidence of the (non-const) pointer usage. */
    FILE *log = fopen("/tmp/logs/3345.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_SymbolLookup_Static\n");
        fprintf(log, "  SymbolName: %s\n", (SymbolName != NULL) ? SymbolName : "(null)");
        fprintf(log, "  ModuleName: %s\n", (ModuleName != NULL) ? ModuleName : "(null)");
        fprintf(log, "  StaticSym (initial pointer): %p\n", (void *)StaticSym);
        fflush(log);
    }

    while (StaticSym != NULL)
    {
        if (log)
        {
            fprintf(log, "LOOP: Inspecting StaticSym at %p\n", (void *)StaticSym);
            fprintf(log, "  StaticSym->Name ptr: %p\n", (void *)StaticSym->Name);
            fprintf(log, "  StaticSym->Module ptr: %p\n", (void *)StaticSym->Module);
            /* Print Address as integer to avoid assuming pointer/integer type */
            fprintf(log, "  StaticSym->Address (raw): 0x%llx\n", (unsigned long long)StaticSym->Address);
            fprintf(log, "  Name string: %s\n", (StaticSym->Name != NULL) ? StaticSym->Name : "(null)");
            fprintf(log, "  Module string: %s\n", (StaticSym->Module != NULL) ? StaticSym->Module : "(null)");
            fflush(log);
        }

        if (StaticSym->Name == NULL)
        {
            /* end of list --
             * Return "OS_ERROR" to indicate that an actual search was done
             * with a not-found result, vs. not searching at all. */
            if (log)
            {
                fprintf(log, "CONDITION: StaticSym->Name == NULL -> end of list. Setting return_code = OS_ERROR\n");
                fflush(log);
            }

            return_code = OS_ERROR;
            break;
        }

        /* Record comparison inputs and results */
        int name_match = (strcmp(StaticSym->Name, SymbolName) == 0);
        int module_match = (ModuleName == NULL) || (strcmp(StaticSym->Module, ModuleName) == 0);
        if (log)
        {
            fprintf(log, "COMPARE: strcmp(Name, SymbolName) => %d\n", name_match);
            fprintf(log, "         ModuleName == NULL ? %d ; strcmp(Module, ModuleName) => %d\n",
                    (ModuleName == NULL) ? 1 : 0,
                    (ModuleName != NULL) ? (int)(strcmp(StaticSym->Module, ModuleName) == 0) : 0);
            fflush(log);
        }

        if (name_match && module_match)
        {
            /* found matching symbol */
            *SymbolAddress = (cpuaddr)StaticSym->Address;
            return_code    = OS_SUCCESS;

            if (log)
            {
                fprintf(log, "MATCH FOUND: Setting *SymbolAddress = 0x%llx ; return_code = OS_SUCCESS\n",
                        (unsigned long long)*SymbolAddress);
                fflush(log);
            }

            break;
        }

        ++StaticSym;
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_SymbolLookup_Static returning %ld\n", (long)return_code);
        fprintf(log, "----------------------------------------\n");
        fclose(log);
    }

    return return_code;
}
