int32 OS_SymbolLookup_Static(cpuaddr *SymbolAddress, const char *SymbolName, const char *ModuleName)
{
    int32                      return_code = OS_ERR_NOT_IMPLEMENTED;
    OS_static_symbol_record_t *StaticSym   = OS_STATIC_SYMTABLE_SOURCE;

    /* Logging setup. Try primary path, fallback if directory doesn't exist. */
    FILE *log = fopen("/tmp/logs/3345.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/3345.txt", "a");
    }

    if (log != NULL)
    {
        fprintf(log, "ENTER OS_SymbolLookup_Static: SymbolName=%s ModuleName=%s StaticSym_ptr=%p\n",
                (SymbolName != NULL) ? SymbolName : "(null)",
                (ModuleName != NULL) ? ModuleName : "(null)",
                (void *)StaticSym);
        fflush(log);
    }

    int found_flag = 0; /* set to 1 when a symbol is found and SymbolAddress is set */

    while (StaticSym != NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "LOOP: StaticSym=%p Name_ptr=%p\n", (void *)StaticSym, (void *)StaticSym->Name);
            if (StaticSym->Name != NULL)
            {
                fprintf(log, "  Name=\"%s\" Module_ptr=%p Module=\"%s\" Address=%p\n",
                        StaticSym->Name,
                        (void *)StaticSym->Module,
                        (StaticSym->Module != NULL) ? StaticSym->Module : "(null)",
                        (void *)StaticSym->Address);
            }
            else
            {
                fprintf(log, "  Name is NULL (end marker)\n");
            }
            fflush(log);
        }

        if (StaticSym->Name == NULL)
        {
            /* end of list --
             * Return "OS_ERROR" to indicate that an actual search was done
             * with a not-found result, vs. not searching at all. */
            if (log != NULL)
            {
                fprintf(log, "End of symbol table reached: returning OS_ERROR\n");
                fflush(log);
            }
            return_code = OS_ERROR;
            break;
        }

        if (strcmp(StaticSym->Name, SymbolName) == 0 &&
            (ModuleName == NULL || strcmp(StaticSym->Module, ModuleName) == 0))
        {
            /* found matching symbol */
            if (log != NULL)
            {
                fprintf(log, "Match found: Name=\"%s\" Module=\"%s\" Address=%p\n",
                        StaticSym->Name,
                        (StaticSym->Module != NULL) ? StaticSym->Module : "(null)",
                        (void *)StaticSym->Address);
                fflush(log);
            }

            *SymbolAddress = (cpuaddr)StaticSym->Address;
            found_flag = 1;
            return_code    = OS_SUCCESS;
            break;
        }

        if (log != NULL)
        {
            fprintf(log, "No match, advancing pointer\n");
            fflush(log);
        }

        ++StaticSym;
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_SymbolLookup_Static: return_code=%d SymbolAddress_set=%d SymbolAddress_ptr=%p\n",
                (int)return_code,
                found_flag,
                (void *)SymbolAddress);
        fflush(log);
        fclose(log);
    }

    return return_code;
}
