int32 OS_SymbolLookup_Static(cpuaddr *SymbolAddress, const char *SymbolName, const char *ModuleName)
{
    int32                      return_code = OS_ERR_NOT_IMPLEMENTED;
    OS_static_symbol_record_t *StaticSym   = OS_STATIC_SYMTABLE_SOURCE;
    FILE *log = fopen("/tmp/logs/3345.txt", "a");
    int iter = 0;

    if (log != NULL)
    {
        if (SymbolName != NULL)
        {
            fprintf(log, "[OS_SymbolLookup_Static] entry: SymbolAddress_ptr=%p, SymbolName_ptr=%p, SymbolName=\"%s\", ModuleName_ptr=%p, ModuleName=\"%s\"\n",
                    (void *)SymbolAddress, (void *)SymbolName, SymbolName, (void *)ModuleName, (ModuleName != NULL) ? ModuleName : "(null)");
        }
        else
        {
            fprintf(log, "[OS_SymbolLookup_Static] entry: SymbolAddress_ptr=%p, SymbolName_ptr=(null), ModuleName_ptr=%p, ModuleName=\"%s\"\n",
                    (void *)SymbolAddress, (void *)ModuleName, (ModuleName != NULL) ? ModuleName : "(null)");
        }
        fflush(log);
    }

    while (StaticSym != NULL)
    {
        if (log != NULL)
        {
            /* Log the current table entry pointer and the Name pointer (but do not dereference Module pointer) */
            fprintf(log, "[OS_SymbolLookup_Static] iter=%d StaticSym_ptr=%p Name_ptr=%p",
                    iter, (void *)StaticSym, (void *)StaticSym->Name);
            if (StaticSym->Name != NULL)
            {
                /* Safe to print Name because we check for NULL first; this matches original code's dereference behavior for Name in strcmp */
                fprintf(log, " Name=\"%s\"", StaticSym->Name);
            }
            else
            {
                fprintf(log, " Name=(null)");
            }

            fprintf(log, " Module_ptr=%p\n", (void *)StaticSym->Module);
            fflush(log);
        }

        if (StaticSym->Name == NULL)
        {
            /* end of list --
             * Return "OS_ERROR" to indicate that an actual search was done
             * with a not-found result, vs. not searching at all. */
            if (log != NULL)
            {
                fprintf(log, "[OS_SymbolLookup_Static] reached end of static symtable (Name == NULL). Setting return_code = OS_ERROR\n");
                fflush(log);
            }

            return_code = OS_ERROR;
            break;
        }

        if (log != NULL)
        {
            /* Log that we are about to compare the stored Name with the requested SymbolName */
            fprintf(log, "[OS_SymbolLookup_Static] comparing stored Name_ptr=%p to SymbolName_ptr=%p\n",
                    (void *)StaticSym->Name, (void *)SymbolName);
            if (ModuleName != NULL)
            {
                fprintf(log, "[OS_SymbolLookup_Static] ModuleName provided, will compare stored Module_ptr=%p to ModuleName_ptr=%p\n",
                        (void *)StaticSym->Module, (void *)ModuleName);
            }
            else
            {
                fprintf(log, "[OS_SymbolLookup_Static] ModuleName is NULL, any module matches\n");
            }
            fflush(log);
        }

        if (strcmp(StaticSym->Name, SymbolName) == 0 &&
            (ModuleName == NULL || strcmp(StaticSym->Module, ModuleName) == 0))
        {
            /* found matching symbol */
            *SymbolAddress = (cpuaddr)StaticSym->Address;
            return_code    = OS_SUCCESS;

            if (log != NULL)
            {
                fprintf(log, "[OS_SymbolLookup_Static] match found at StaticSym_ptr=%p; assigning *SymbolAddress (ptr=%p) = StaticSym->Address (addr_ptr=%p); return_code=OS_SUCCESS\n",
                        (void *)StaticSym, (void *)SymbolAddress, (void *)StaticSym->Address);
                fflush(log);
            }

            break;
        }

        if (log != NULL)
        {
            fprintf(log, "[OS_SymbolLookup_Static] no match for this entry; advancing to next\n");
            fflush(log);
        }

        ++StaticSym;
        ++iter;
    }

    if (log != NULL)
    {
        fprintf(log, "[OS_SymbolLookup_Static] exit: return_code=%d\n", return_code);
        fclose(log);
    }

    return return_code;
}
