int32 OS_SymbolLookup_Static(cpuaddr *SymbolAddress, const char *SymbolName, const char *ModuleName)
{
    int32                      return_code = OS_ERR_NOT_IMPLEMENTED;
    OS_static_symbol_record_t *StaticSym   = OS_STATIC_SYMTABLE_SOURCE;

    /* Local declarations for logging functions to avoid requiring additional includes here. */
    typedef struct FILE FILE;
    FILE *fopen(const char *, const char *);
    int fprintf(FILE *, const char *, ...);
    int fclose(FILE *);

    FILE *log = fopen("/tmp/logs/3345.txt", "a");
    if (log)
    {
        fprintf(log, "OS_SymbolLookup_Static entered: SymbolName=%s, ModuleName=%s\n",
                SymbolName ? SymbolName : "(null)", ModuleName ? ModuleName : "(null)");
    }

    int index = 0;
    while (StaticSym != NULL)
    {
        if (log)
        {
            /* Log pointer values and available strings safely. */
            fprintf(log, "iter %d: StaticSym ptr=%p, Name ptr=%p, Module ptr=%p, Address ptr=%p\n",
                    index,
                    (void *)StaticSym,
                    (void *)StaticSym->Name,
                    (void *)StaticSym->Module,
                    (void *)StaticSym->Address);

            fprintf(log, "iter %d: Name='%s', Module='%s'\n",
                    index,
                    StaticSym->Name ? StaticSym->Name : "(null)",
                    StaticSym->Module ? StaticSym->Module : "(null)");
        }

        if (StaticSym->Name == NULL)
        {
            /* end of list --
             * Return "OS_ERROR" to indicate that an actual search was done
             * with a not-found result, vs. not searching at all. */
            if (log) fprintf(log, "iter %d: End of list (Name==NULL). Setting return_code=OS_ERROR\n", index);
            return_code = OS_ERROR;
            break;
        }

        if (strcmp(StaticSym->Name, SymbolName) == 0 &&
            (ModuleName == NULL || strcmp(StaticSym->Module, ModuleName) == 0))
        {
            /* found matching symbol */
            if (log) fprintf(log, "iter %d: Found matching symbol. Address=%p\n", index, (void *)StaticSym->Address);
            *SymbolAddress = (cpuaddr)StaticSym->Address;
            return_code    = OS_SUCCESS;
            break;
        }

        if (log)
        {
            fprintf(log, "iter %d: No match. Name='%s' vs SymbolName='%s'\n",
                    index,
                    StaticSym->Name ? StaticSym->Name : "(null)",
                    SymbolName ? SymbolName : "(null)");
        }

        ++StaticSym;
        ++index;
    }

    if (log)
    {
        fprintf(log, "OS_SymbolLookup_Static exiting with code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
